import * as S from 'sweet-spec';
import * as _ from 'ramda';
import { List } from 'immutable';
import Syntax from './syntax';
import codegen, { FormattedCodeGen } from 'shift-codegen';
import SweetToShiftReducer from './sweet-to-shift-reducer';
import TermExpander from './term-expander';
import Env from './env';

import { replaceTemplate } from './template-processor';

export function expandCompiletime(term, context) {
  // each compiletime value needs to be expanded with a fresh
  // environment and in the next higher phase
  let syntaxExpander = new TermExpander(
    _.merge(context, {
      phase: context.phase + 1,
      env: new Env(),
      store: context.store,
    }),
  );

  return syntaxExpander.expand(term);
}

export function sanitizeReplacementValues(values) {
  if (Array.isArray(values)) {
    return sanitizeReplacementValues(List(values));
  } else if (List.isList(values)) {
    return values.map(sanitizeReplacementValues);
  } else if (values == null) {
    throw new Error(
      'replacement values for syntax template must not be null or undefined',
    );
  } else if (typeof values.next === 'function') {
    return sanitizeReplacementValues(List(values));
  }
  return values;
}

// (Expression, Context) -> [function]
export function evalCompiletimeValue(expr: S.Expression, context: any) {
  let sandbox = {
    syntaxTemplate: function(ident, ...values) {
      return replaceTemplate(
        context.templateMap.get(ident),
        sanitizeReplacementValues(values),
      );
    },
  };

  let sandboxKeys = List(Object.keys(sandbox));
  let sandboxVals = sandboxKeys.map(k => sandbox[k]).toArray();

  let parsed = new S.Module({
    directives: List(),
    items: List.of(
      new S.ExpressionStatement({
        expression: new S.FunctionExpression({
          isGenerator: false,
          name: null,
          params: new S.FormalParameters({
            items: sandboxKeys.map(param => {
              return new S.BindingIdentifier({
                name: Syntax.from('identifier', param),
              });
            }),
            rest: null,
          }),
          body: new S.FunctionBody({
            directives: List.of(
              new S.Directive({
                rawValue: 'use strict',
              }),
            ),
            statements: List.of(
              new S.ReturnStatement({
                expression: expr,
              }),
            ),
          }),
        }),
      }),
    ),
  }).reduce(new SweetToShiftReducer(context.phase));

  let gen = codegen(parsed, new FormattedCodeGen());
  let result = context.transform(gen);

  let val = context.loader.eval(result.code, context.store);
  return val.apply(undefined, sandboxVals);
}

import { expect } from './errors';
import { List } from 'immutable';
import { Enforester } from './enforester';
import { ALL_PHASES } from './syntax';
import * as _ from 'ramda';
import ScopeReducer from './scope-reducer';
import * as T from 'sweet-spec';
import Term, * as S from 'sweet-spec';
import Syntax from './syntax';
import { isTemplate, isDelimiter, getKind } from './tokens';
import type { TokenTree } from './tokens';

export function wrapInTerms(stx: List<TokenTree>): List<Term> {
  return stx.map(s => {
    if (isTemplate(s)) {
      if (s.items) {
        s.items = wrapInTerms(s.items);
        return new T.RawSyntax({
          value: new Syntax(s),
        });
      }
      return new T.RawSyntax({
        value: new Syntax(s),
      });
    } else if (isDelimiter(s)) {
      return new S.RawDelimiter({
        kind: getKind(s),
        inner: wrapInTerms(s),
      });
    }
    return new S.RawSyntax({
      value: new Syntax(s),
    });
  });
}

const privateData = new WeakMap();

function cloneEnforester(enf) {
  const { rest, prev, context } = enf;
  return new Enforester(rest, prev, context);
}

function Marker() {}

/*
ctx :: {
  of: (Syntax) -> ctx
  next: (String) -> Syntax or Term
}
*/
export default class MacroContext {
  constructor(enf, name, context, useScope, introducedScope) {
    const startMarker = new Marker();
    const startEnf = cloneEnforester(enf);
    const priv = {
      name,
      context,
      enf: startEnf,
      startMarker,
      markers: new Map([[startMarker, enf]]),
    };

    if (useScope && introducedScope) {
      priv.noScopes = false;
      priv.useScope = useScope;
      priv.introducedScope = introducedScope;
    } else {
      priv.noScopes = true;
    }
    privateData.set(this, priv);
    this.reset(); // set current enforester

    this[Symbol.iterator] = () => this;
  }

  name() {
    const { name } = privateData.get(this);
    return new T.RawSyntax({ value: name });
  }

  contextify(delim: any) {
    if (!(delim instanceof T.RawDelimiter)) {
      throw new Error(`Can only contextify a delimiter but got ${delim}`);
    }
    const { context } = privateData.get(this);

    let enf = new Enforester(
      delim.inner.slice(1, delim.inner.size - 1),
      List(),
      context,
    );
    return new MacroContext(enf, 'inner', context);
  }

  expand(type) {
    const { enf } = privateData.get(this);
    if (enf.rest.size === 0) {
      return {
        done: true,
        value: null,
      };
    }
    enf.expandMacro();
    let originalRest = enf.rest;
    let value;
    switch (type) {
      case 'AssignmentExpression':
      case 'expr':
        value = enf.enforestExpressionLoop();
        break;
      case 'Expression':
        value = enf.enforestExpression();
        break;
      case 'Statement':
      case 'stmt':
        value = enf.enforestStatement();
        break;
      case 'BlockStatement':
      case 'WhileStatement':
      case 'IfStatement':
      case 'ForStatement':
      case 'SwitchStatement':
      case 'BreakStatement':
      case 'ContinueStatement':
      case 'DebuggerStatement':
      case 'WithStatement':
      case 'TryStatement':
      case 'ThrowStatement':
      case 'ClassDeclaration':
      case 'FunctionDeclaration':
      case 'LabeledStatement':
      case 'VariableDeclarationStatement':
      case 'ReturnStatement':
      case 'ExpressionStatement':
        value = enf.enforestStatement();
        expect(
          _.whereEq({ type }, value),
          `Expecting a ${type}`,
          value,
          originalRest,
        );
        break;
      case 'YieldExpression':
        value = enf.enforestYieldExpression();
        break;
      case 'ClassExpression':
        value = enf.enforestClass({ isExpr: true });
        break;
      case 'ArrowExpression':
        value = enf.enforestArrowExpression();
        break;
      case 'NewExpression':
        value = enf.enforestNewExpression();
        break;
      case 'ThisExpression':
      case 'FunctionExpression':
      case 'IdentifierExpression':
      case 'LiteralNumericExpression':
      case 'LiteralInfinityExpression':
      case 'LiteralStringExpression':
      case 'TemplateExpression':
      case 'LiteralBooleanExpression':
      case 'LiteralNullExpression':
      case 'LiteralRegExpExpression':
      case 'ObjectExpression':
      case 'ArrayExpression':
        value = enf.enforestPrimaryExpression();
        break;
      case 'UnaryExpression':
      case 'UpdateExpression':
      case 'BinaryExpression':
      case 'StaticMemberExpression':
      case 'ComputedMemberExpression':
      case 'CompoundAssignmentExpression':
      case 'ConditionalExpression':
        value = enf.enforestExpressionLoop();
        expect(
          _.whereEq({ type }, value),
          `Expecting a ${type}`,
          value,
          originalRest,
        );
        break;
      default:
        throw new Error('Unknown term type: ' + type);
    }
    return {
      done: false,
      value: value,
    };
  }

  _rest(enf) {
    const priv = privateData.get(this);
    if (priv.markers.get(priv.startMarker) === enf) {
      return priv.enf.rest;
    }
    throw Error('Unauthorized access!');
  }

  reset(marker) {
    const priv = privateData.get(this);
    let enf;
    if (marker == null) {
      // go to the beginning
      enf = priv.markers.get(priv.startMarker);
    } else if (marker && marker instanceof Marker) {
      // marker could be from another context
      if (priv.markers.has(marker)) {
        enf = priv.markers.get(marker);
      } else {
        throw new Error('marker must originate from this context');
      }
    } else {
      throw new Error('marker must be an instance of Marker');
    }
    priv.enf = cloneEnforester(enf);
  }

  mark() {
    const priv = privateData.get(this);
    let marker;

    // the idea here is that marking at the beginning shouldn't happen more than once.
    // We can reuse startMarker.
    if (priv.enf.rest === priv.markers.get(priv.startMarker).rest) {
      marker = priv.startMarker;
    } else if (priv.enf.rest.isEmpty()) {
      // same reason as above
      if (!priv.endMarker) priv.endMarker = new Marker();
      marker = priv.endMarker;
    } else {
      //TODO(optimization/dubious): check that there isn't already a marker for this index?
      marker = new Marker();
    }
    if (!priv.markers.has(marker)) {
      priv.markers.set(marker, cloneEnforester(priv.enf));
    }
    return marker;
  }

  next() {
    const {
      enf,
      noScopes,
      useScope,
      introducedScope,
      context,
    } = privateData.get(this);
    if (enf.rest.size === 0) {
      return {
        done: true,
        value: null,
      };
    }
    let value = enf.advance();
    if (!noScopes) {
      value = value.reduce(
        new ScopeReducer(
          [
            { scope: useScope, phase: ALL_PHASES, flip: false },
            { scope: introducedScope, phase: ALL_PHASES, flip: true },
          ],
          context.bindings,
        ),
      );
    }
    return {
      done: false,
      value: value,
    };
  }
}

// @flow
import { evalCompiletimeValue } from './load-syntax';
import * as _ from 'ramda';
import * as T from 'sweet-spec';
import * as S from './sweet-spec-utils';
import { gensym } from './symbol';
import { ModuleNamespaceTransform, CompiletimeTransform } from './transforms';
import { collectBindings } from './hygiene-utils';
import SweetModule from './sweet-module';
import { List } from 'immutable';
import SweetToShiftReducer from './sweet-to-shift-reducer';
import codegen, { FormattedCodeGen } from 'shift-codegen';

import type { Context } from './sweet-loader';

export function bindImports(
  impTerm: T.ImportDeclaration,
  exModule: SweetModule,
  phase: any,
  context: Context,
) {
  let names = [];
  let phaseToBind = impTerm.forSyntax ? phase + 1 : phase;
  if (impTerm.defaultBinding != null) {
    let exportName = exModule.exportedNames.find(
      exName => exName.exportedName.val() === '_default',
    );
    let name = impTerm.defaultBinding.name;
    if (exportName != null) {
      let newBinding = gensym('_default');
      let toForward = exportName.exportedName;
      context.bindings.addForward(name, toForward, newBinding, phaseToBind);
      names.push(name);
    }
  }
  if (impTerm.namedImports) {
    impTerm.namedImports.forEach(specifier => {
      let name = specifier.binding.name;
      let exportName = exModule.exportedNames.find(
        exName => exName.exportedName.val() === name.val(),
      );
      if (exportName != null) {
        let newBinding = gensym(name.val());
        let toForward = exportName.name
          ? exportName.name
          : exportName.exportedName;
        context.bindings.addForward(name, toForward, newBinding, phaseToBind);
        names.push(name);
      }
    });
  }
  if (impTerm.namespaceBinding) {
    let name = impTerm.namespaceBinding.name;
    let newBinding = gensym(name.val());
    context.store.set(
      newBinding.toString(),
      new ModuleNamespaceTransform(name, exModule),
    );
    context.bindings.add(name, {
      binding: newBinding,
      phase: phaseToBind,
      skipDup: false,
    });

    names.push(name);
  }
  return List(names);
}

export default class {
  context: Context;

  constructor(context: Context) {
    this.context = context;
  }

  visit(mod: SweetModule, phase: any, store: any) {
    mod.imports.forEach(imp => {
      if (imp.forSyntax) {
        let mod = this.context.loader.get(
          imp.moduleSpecifier.val(),
          phase + 1,
          '',
        );
        this.visit(mod, phase + 1, store);
        this.invoke(mod, phase + 1, store);
      } else {
        let mod = this.context.loader.get(imp.moduleSpecifier.val(), phase, '');
        this.visit(mod, phase, store);
      }
      bindImports(imp, mod, phase, this.context);
    });
    for (let term of mod.compiletimeItems()) {
      if (S.isSyntaxDeclarationStatement(term)) {
        this.registerSyntaxDeclaration(term.declaration, phase, store);
      }
    }
    return store;
  }

  invoke(mod: any, phase: any, store: any) {
    mod.imports.forEach(imp => {
      if (!imp.forSyntax) {
        let mod = this.context.loader.get(imp.moduleSpecifier.val(), phase, '');
        this.invoke(mod, phase, store);
        bindImports(imp, mod, phase, this.context);
      }
    });
    let items = mod.runtimeItems();
    for (let term of items) {
      if (S.isVariableDeclarationStatement(term)) {
        this.registerVariableDeclaration(term.declaration, phase, store);
      } else if (S.isFunctionDeclaration(term)) {
        this.registerFunctionOrClass(term, phase, store);
      }
    }
    let parsed = new T.Module({
      directives: List(),
      items,
    }).reduce(new SweetToShiftReducer(phase));

    let gen = codegen(parsed, new FormattedCodeGen());
    let result = this.context.transform(gen);

    this.context.loader.eval(result.code, store);
    return store;
  }

  registerSyntaxDeclaration(
    term: T.VariableDeclarationStatement,
    phase: any,
    store: any,
  ) {
    term.declarators.forEach(decl => {
      let val = evalCompiletimeValue(
        decl.init,
        _.merge(this.context, {
          phase: phase + 1,
          store,
        }),
      );

      collectBindings(decl.binding).forEach(stx => {
        if (phase !== 0) {
          // phase 0 bindings extend the binding map during compilation
          let newBinding = gensym(stx.val());
          this.context.bindings.add(stx, {
            binding: newBinding,
            phase: phase,
            skipDup: false,
          });
        }
        let resolvedName = stx.resolve(phase);
        store.set(
          resolvedName,
          new CompiletimeTransform({ type: 'syntax', f: val }),
        );
      });
    });
  }

  registerVariableDeclaration(term: any, phase: any, store: any) {
    term.declarators.forEach(decl => {
      collectBindings(decl.binding).forEach(stx => {
        if (phase !== 0) {
          // phase 0 bindings extend the binding map during compilation
          let newBinding = gensym(stx.val());
          this.context.bindings.add(stx, {
            binding: newBinding,
            phase: phase,
            skipDup: term.kind === 'var',
          });
        }
      });
    });
  }

  registerFunctionOrClass(term: any, phase: any, store: any) {
    collectBindings(term.name).forEach(stx => {
      if (phase !== 0) {
        let newBinding = gensym(stx.val());
        this.context.bindings.add(stx, {
          binding: newBinding,
          phase: phase,
          skipDup: false,
        });
      }
    });
  }
}

// @flow
import SweetLoader, { phaseInModulePathRegexp } from './sweet-loader';
import { readFileSync } from 'fs';
import { dirname } from 'path';
import resolve from 'resolve';
import vm from 'vm';
import Store from './store';
import type { LoaderOptions } from './sweet-loader';

type NodeLoaderOptions = LoaderOptions & { extensions?: Array<string> };

export default class NodeLoader extends SweetLoader {
  extensions: Array<string>;

  constructor(baseDir: string, options: NodeLoaderOptions = {}) {
    super(baseDir, options);
    this.extensions = options.extensions || [];
  }

  normalize(name: string, refererName?: string, refererAddress?: string) {
    let normName = super.normalize(name, refererName, refererAddress);
    let match = normName.match(phaseInModulePathRegexp);
    if (match && match.length >= 3) {
      let resolvedName = resolve.sync(match[1], {
        basedir: refererName ? dirname(refererName) : this.baseDir,
        extensions: this.extensions ? this.extensions : ['.js'],
      });
      return `${resolvedName}:${match[2]}`;
    }
    throw new Error(`Module ${name} is missing phase information`);
  }

  fetch({
    name,
    address,
    metadata,
  }: { name: string, address: { path: string, phase: number }, metadata: {} }) {
    let src = this.sourceCache.get(address.path);
    if (src != null) {
      return src;
    } else {
      src = readFileSync(address.path, 'utf8');
      this.sourceCache.set(address.path, src);
      return src;
    }
  }

  freshStore() {
    let sandbox = {
      process: global.process,
      console: global.console,
    };
    return new Store(vm.createContext(sandbox));
  }

  eval(source: string, store: Store) {
    return vm.runInContext(source, store.getBackingObject());
  }
}

import { readFileSync } from 'fs';

