import SweetLoader from './sweet-loader';
import vm from 'vm';
import Store from './store';

export default class extends SweetLoader {
  store: Map<string, string>;

  constructor(
    baseDir: string,
    store: Map<string, string>,
    noBabel: boolean = false,
  ) {
    super(baseDir, { noBabel });
    this.store = store;
  }

  fetch({ name, address }: { name: string, address: any }) {
    if (this.store.has(address.path)) {
      return this.store.get(address.path);
    }
    throw new Error(
      `The module ${name} is not in the debug store: addr.path is ${address.path}`,
    );
  }

  freshStore() {
    return new Store(vm.createContext());
  }

  eval(source: string, store: Store) {
    return vm.runInContext(source, store.getBackingObject());
  }
}

// @flow
import * as T from 'sweet-spec';
import * as _ from 'ramda';
import * as S from './sweet-spec-utils';
import codegen from './codegen';
import { List } from 'immutable';
import SweetToShiftReducer from './sweet-to-shift-reducer.js';
import Syntax from './syntax';

const extractDeclaration = _.cond([
  [S.isExport, _.prop('declaration')],
  [S.isExportDefault, _.prop('body')],
  [
    _.T,
    term => {
      throw new Error(`Expecting an Export or ExportDefault but got ${term}`);
    },
  ],
]);

const ExpSpec = x => ({ exportedName: x });

const extractDeclarationNames = _.cond([
  [S.isVariableDeclarator, ({ binding }) => List.of(ExpSpec(binding.name))],
  [
    S.isVariableDeclaration,
    ({ declarators }) => declarators.flatMap(extractDeclarationNames),
  ],
  [S.isFunctionDeclaration, ({ name }) => List.of(ExpSpec(name.name))],
  [S.isClassDeclaration, ({ name }) => List.of(ExpSpec(name.name))],
]);

type ExportSpecifier = {
  name?: Syntax,
  exportedName: Syntax,
};

function extractNames(term: T.ExportDeclaration): List<ExportSpecifier> {
  if (S.isExport(term)) {
    return extractDeclarationNames(term.declaration);
  } else if (S.isExportDefault(term)) {
    return List();
  } else if (S.isExportFrom(term)) {
    return term.namedExports;
  }
  throw new Error(`Unknown export type`);
}

function wrapStatement(declaration: T.Term) {
  if (S.isVariableDeclaration(declaration)) {
    return new T.VariableDeclarationStatement({ declaration });
  }
  return declaration;
}

const memoSym = Symbol('memo');

function makeVarDeclStmt(name: T.BindingIdentifier, expr: T.Expression) {
  return new T.VariableDeclarationStatement({
    declaration: new T.VariableDeclaration({
      kind: 'var',
      declarators: List.of(
        new T.VariableDeclarator({
          binding: name,
          init: expr,
        }),
      ),
    }),
  });
}

export default class SweetModule {
  items: List<T.Term>;
  imports: List<T.ImportDeclaration>;
  exports: List<T.ExportDeclaration>;
  exportedNames: List<ExportSpecifier>;

  runtime: List<T.Term>;
  compiletime: List<T.Term>;

  constructor(items: List<T.Term>) {
    let body = [];
    let imports = [];
    let exports = [];
    this.exportedNames = List();
    for (let item of items) {
      if (S.isImportDeclaration(item)) {
        imports.push(item);
      } else if (S.isExportDeclaration(item)) {
        exports.push(item);
        this.exportedNames = this.exportedNames.concat(extractNames(item));
        if (S.isExport(item)) {
          body.push(wrapStatement(extractDeclaration(item)));
        } else if (S.isExportDefault(item)) {
          let decl = extractDeclaration(item);
          let defStx = Syntax.fromIdentifier('_default');
          let def = new T.BindingIdentifier({
            name: defStx,
          });
          this.exportedNames = this.exportedNames.push(ExpSpec(defStx));
          if (S.isFunctionDeclaration(decl) || S.isClassDeclaration(decl)) {
            body.push(decl);
            // extract name and bind it to _default
            body.push(
              makeVarDeclStmt(
                def,
                new T.IdentifierExpression({ name: decl.name.name }),
              ),
            );
          } else {
            // expression so bind it to _default
            body.push(makeVarDeclStmt(def, decl));
          }
        }
      } else {
        body.push(item);
      }
    }
    this.items = List(body);
    this.imports = List(imports);
    this.exports = List(exports);
  }

  // $FlowFixMe: flow doesn't support computed property keys yet
  [memoSym]() {
    let runtime = [], compiletime = [];
    for (let item of this.items) {
      if (S.isCompiletimeStatement(item)) {
        compiletime.push(item);
      } else {
        runtime.push(item);
      }
    }
    this.runtime = List(runtime);
    this.compiletime = List(compiletime);
  }

  runtimeItems() {
    if (this.runtime == null) {
      // $FlowFixMe: flow doesn't support computed property keys yet
      this[memoSym]();
    }
    return this.runtime;
  }

  compiletimeItems() {
    if (this.compiletime == null) {
      // $FlowFixMe: flow doesn't support computed property keys yet
      this[memoSym]();
    }
    return this.compiletime;
  }

  parse() {
    return new T.Module({
      items: this.items,
      directives: List(),
    }).reduce(new SweetToShiftReducer(0));
  }

  codegen() {
    return codegen(this.parse()).code;
  }
}

// @flow

import * as T from 'sweet-spec';
import * as _ from 'ramda';

export const isImportDeclaration = _.is(T.ImportDeclaration);

export const isExportDeclaration = _.is(T.ExportDeclaration);
export const isExport = _.is(T.Export);
export const isExportDefault = _.is(T.ExportDefault);
export const isExportFrom = _.is(T.ExportFrom);

export const isVariableDeclaration = _.is(T.VariableDeclaration);
export const isVariableDeclarator = _.is(T.VariableDeclarator);
export const isSyntaxVariableDeclartion = _.both(
  isVariableDeclaration,
  _.either(_.propEq('kind', 'syntax'), _.propEq('kind', 'syntaxrec')),
);

export const isVariableDeclarationStatement = _.is(
  T.VariableDeclarationStatement,
);
export const isSyntaxDeclarationStatement = (term: any) => {
  // syntax m = ...
  // syntaxrec m = ...
  return (
    isVariableDeclarationStatement(term) &&
    isSyntaxVariableDeclartion(term.declaration)
  );
};

export const isCompiletimeStatement = isSyntaxDeclarationStatement;

export const isFunctionDeclaration = _.is(T.FunctionDeclaration);
export const isClassDeclaration = _.is(T.ClassDeclaration);

// @flow
import Term, * as S from 'sweet-spec';
import { complement } from 'ramda';
import { List } from 'immutable';

import { isEmptyStatement } from './terms';

import type Syntax from './syntax.js';

const notEmptyStatement = complement(isEmptyStatement);

export default class extends Term.CloneReducer {
  phase: number;

  constructor(phase: number) {
    super();
    this.phase = phase;
  }

  reduceModule(t: Term, s: { directives: List<any>, items: List<any> }) {
    return new S.Module({
      directives: s.directives.toArray(),
      items: s.items.toArray().filter(notEmptyStatement),
    });
  }

  reduceIdentifierExpression(t: Term, s: { name: Syntax }) {
    return new S.IdentifierExpression({
      name: s.name.resolve(this.phase),
    });
  }

  reduceStaticPropertyName(t: Term, s: { value: Syntax }) {
    return new S.StaticPropertyName({
      value: s.value.val().toString(),
    });
  }

  reduceBindingIdentifier(t: Term, s: { name: Syntax }) {
    return new S.BindingIdentifier({
      name: s.name.resolve(this.phase),
    });
  }

  reduceStaticMemberExpression(t: Term, s: { object: any, property: Syntax }) {
    return new S.StaticMemberExpression({
      object: s.object,
      property: s.property.val(),
    });
  }

  reduceFunctionBody(
    t: Term,
    s: { statements: List<any>, directives: List<any> },
  ) {
    return new S.FunctionBody({
      directives: s.directives.toArray(),
      statements: s.statements.toArray().filter(notEmptyStatement),
    });
  }

  reduceVariableDeclarationStatement(t: Term, s: { declaration: any }) {
    if (
      t.declaration.kind === 'syntax' ||
      t.declaration.kind === 'syntaxrec' ||
      t.declaration.kind === 'operator'
    ) {
      return new S.EmptyStatement();
    }
    return new S.VariableDeclarationStatement({
      declaration: s.declaration,
    });
  }

  reduceVariableDeclaration(t: Term, s: { kind: any, declarators: List<any> }) {
    return new S.VariableDeclaration({
      kind: s.kind,
      declarators: s.declarators.toArray(),
    });
  }

  reduceCallExpression(t: Term, s: { callee: any, arguments: List<any> }) {
    return new S.CallExpression({
      callee: s.callee,
      arguments: s.arguments.toArray(),
    });
  }

  reduceArrayExpression(t: Term, s: { elements: List<any> }) {
    return new S.ArrayExpression({
      elements: s.elements.toArray(),
    });
  }

  reduceImport() {
    return new S.EmptyStatement({});
  }

  reduceBlock(t: Term, s: { statements: List<any> }) {
    return new S.Block({
      statements: s.statements.toArray().filter(notEmptyStatement),
    });
  }
}

// @flow
let internedMap: Map<string, Symbol> = new Map();

let counter = 0;

export function gensym(name: string) {
  let prefix = name == null ? 's_' : name + '_';
  let sym = new Symbol(prefix + counter);
  counter++;
  return sym;
}

class Symbol {
  name: string;

  constructor(name: string) {
    this.name = name;
  }
  toString() {
    return this.name;
  }
}

function makeSymbol(name: string): Symbol {
  let s = internedMap.get(name);
  if (s) {
    return s;
  } else {
    let sym = new Symbol(name);
    internedMap.set(name, sym);
    return sym;
  }
}

export { makeSymbol as Symbol, Symbol as SymbolClass };

// @flow
import { List, Map } from 'immutable';
import { assert } from './errors';
import BindingMap from './binding-map';
import { Maybe } from 'ramda-fantasy';
import * as _ from 'ramda';
import * as T from 'sweet-spec';

import { TokenType, TokenClass, TypeCodes } from './tokens';

type Token = {
  type: any,
  value: any,
  slice: any,
};

type TokenTag =
  | 'null'
  | 'number'
  | 'string'
  | 'punctuator'
  | 'keyword'
  | 'identifier'
  | 'regularExpression'
  | 'boolean'
  | 'braces'
  | 'parens'
  | 'delimiter'
  | 'eof'
  | 'template'
  | 'assign'
  | 'syntaxTemplate'
  | 'brackets';

function getFirstSlice(stx: ?Syntax) {
  if (!stx || typeof stx.isDelimiter !== 'function') return null; // TODO: should not have to do this
  if (!stx.isDelimiter()) {
    return stx.token.slice;
  }
  return stx.token.get(0).token.slice;
}

function sizeDecending(a, b) {
  if (a.scopes.size > b.scopes.size) {
    return -1;
  } else if (b.scopes.size > a.scopes.size) {
    return 1;
  } else {
    return 0;
  }
}

type TypesHelper = {
  [key: TokenTag]: {
    match(token: any): boolean,
    create?: (value: any, stx: ?Syntax) => Syntax,
  },
};

export let Types: TypesHelper = {
  null: {
    match: token =>
      !Types.delimiter.match(token) && token.type === TokenType.NULL,
    create: (value, stx) =>
      new Syntax(
        {
          type: TokenType.NULL,
          value: null,
          typeCode: TypeCodes.Keyword,
        },
        stx,
      ),
  },
  number: {
    match: token =>
      !Types.delimiter.match(token) &&
      token.type.klass === TokenClass.NumericLiteral,
    create: (value, stx) =>
      new Syntax(
        {
          type: TokenType.NUMBER,
          value,
          typeCode: TypeCodes.NumericLiteral,
        },
        stx,
      ),
  },
  string: {
    match: token =>
      !Types.delimiter.match(token) &&
      token.type.klass === TokenClass.StringLiteral,
    create: (value, stx) =>
      new Syntax(
        {
          type: TokenType.STRING,
          str: value,
          typeCode: TypeCodes.StringLiteral,
        },
        stx,
      ),
  },
  punctuator: {
    match: token =>
      !Types.delimiter.match(token) &&
      token.type.klass === TokenClass.Punctuator,
    create: (value, stx) =>
      new Syntax(
        {
          type: {
            klass: TokenClass.Punctuator,
            name: value,
          },
          typeCode: TypeCodes.Punctuator,
          value,
        },
        stx,
      ),
  },
  keyword: {
    match: token =>
      !Types.delimiter.match(token) && token.type.klass === TokenClass.Keyword,
    create: (value, stx) =>
      new Syntax(
        {
          type: {
            klass: TokenClass.Keyword,
            name: value,
          },
          typeCode: TypeCodes.Keyword,
          value,
        },
        stx,
      ),
  },
  identifier: {
    match: token =>
      !Types.delimiter.match(token) && token.type.klass === TokenClass.Ident,
    create: (value, stx) =>
      new Syntax(
        {
          type: TokenType.IDENTIFIER,
          value,
          typeCode: TypeCodes.Identifier,
        },
        stx,
      ),
  },
  regularExpression: {
    match: token =>
      !Types.delimiter.match(token) &&
      token.type.klass === TokenClass.RegularExpression,
    create: (value, stx) =>
      new Syntax(
        {
          type: TokenType.REGEXP,
          value,
          typeCode: TypeCodes.RegExp,
        },
        stx,
      ),
  },
  braces: {
    match: token =>
      Types.delimiter.match(token) &&
      token.get(0).token.type === TokenType.LBRACE,
    create: (inner, stx) => {
      let left = new T.RawSyntax({
        value: new Syntax({
          type: TokenType.LBRACE,
          typeCode: TypeCodes.Punctuator,
          value: '{',
          slice: getFirstSlice(stx),
        }),
      });
      let right = new T.RawSyntax({
        value: new Syntax({
          type: TokenType.RBRACE,
          typeCode: TypeCodes.Punctuator,
          value: '}',
          slice: getFirstSlice(stx),
        }),
      });
      return new T.RawDelimiter({
        kind: 'braces',
        inner: List.of(left).concat(inner).push(right),
      });
    },
  },
  brackets: {
    match: token =>
      Types.delimiter.match(token) &&
      token.get(0).token.type === TokenType.LBRACK,
    create: (inner, stx) => {
      let left = new T.RawSyntax({
        value: new Syntax({
          type: TokenType.LBRACK,
          typeCode: TypeCodes.Punctuator,
          value: '[',
          slice: getFirstSlice(stx),
        }),
      });
      let right = new T.RawSyntax({
        value: new Syntax({
          type: TokenType.RBRACK,
          typeCode: TypeCodes.Punctuator,
          value: ']',
          slice: getFirstSlice(stx),
        }),
      });
      return new T.RawDelimiter({
        kind: 'brackets',
        inner: List.of(left).concat(inner).push(right),
      });
    },
  },
  parens: {
    match: token =>
      Types.delimiter.match(token) &&
      token.get(0).token.type === TokenType.LPAREN,
    create: (inner, stx) => {
      let left = new T.RawSyntax({
        value: new Syntax({
          type: TokenType.LPAREN,
          typeCode: TypeCodes.Punctuator,
          value: '(',
          slice: getFirstSlice(stx),
        }),
      });
      let right = new T.RawSyntax({
        value: new Syntax({
          type: TokenType.RPAREN,
          typeCode: TypeCodes.Punctuator,
          value: ')',
          slice: getFirstSlice(stx),
        }),
      });
      return new T.RawDelimiter({
        kind: 'parens',
        inner: List.of(left).concat(inner).push(right),
      });
    },
  },

  assign: {
    match: token => {
      if (Types.punctuator.match(token)) {
        switch (token.value) {
          case '=':
          case '|=':
          case '^=':
          case '&=':
          case '<<=':
          case '>>=':
          case '>>>=':
          case '+=':
          case '-=':
          case '*=':
          case '/=':
          case '%=':
            return true;
          default:
            return false;
        }
      }
      return false;
    },
  },

  boolean: {
    match: token =>
      (!Types.delimiter.match(token) && token.type === TokenType.TRUE) ||
      token.type === TokenType.FALSE,
  },

  template: {
    match: token =>
      !Types.delimiter.match(token) && token.type === TokenType.TEMPLATE,
  },

  delimiter: {
    match: token => List.isList(token),
  },

  syntaxTemplate: {
    match: token => Types.delimiter.match(token) && token.get(0).val() === '#`',
  },

  eof: {
    match: token =>
      !Types.delimiter.match(token) && token.type === TokenType.EOS,
  },
};
export const ALL_PHASES = {};

type Scopeset = {
  all: List<any>,
  phase: Map<number | {}, any>,
};

export default class Syntax {
  // token: Token | List<Token>;
  token: any;
  bindings: BindingMap;
  scopesets: Scopeset;

  constructor(token: any, oldstx: ?{ bindings: any, scopesets: any }) {
    this.token = token;
    this.bindings = oldstx && oldstx.bindings != null
      ? oldstx.bindings
      : new BindingMap();
    this.scopesets = oldstx && oldstx.scopesets != null
      ? oldstx.scopesets
      : {
          all: List(),
          phase: Map(),
        };
    Object.freeze(this);
  }

  static of(token: Token, stx: ?Syntax) {
    return new Syntax(token, stx);
  }

  static from(type, value, stx: ?Syntax) {
    if (!Types[type]) {
      throw new Error(type + ' is not a valid type');
    } else if (!Types[type].create) {
      throw new Error('Cannot create a syntax from type ' + type);
    }
    let newstx = Types[type].create(value, stx);
    let slice = getFirstSlice(stx);
    if (slice != null && newstx.token != null) {
      newstx.token.slice = slice;
    }
    return newstx;
  }

  from(type: TokenTag, value: any) {
    // TODO: this is gross, fix
    let s = Syntax.from(type, value, this);
    if (s instanceof Syntax) {
      return new T.RawSyntax({ value: s });
    }
    return s;
  }

  fromNull() {
    return this.from('null', null);
  }

  fromNumber(value: number) {
    return this.from('number', value);
  }

  fromString(value: string) {
    return this.from('string', value);
  }

  fromPunctuator(value: string) {
    return this.from('punctuator', value);
  }

  fromKeyword(value: string) {
    return this.from('keyword', value);
  }

  fromIdentifier(value: string) {
    return this.from('identifier', value);
  }

  fromRegularExpression(value: any) {
    return this.from('regularExpression', value);
  }

  static fromNull(stx: Syntax) {
    return Syntax.from('null', null, stx);
  }

  static fromNumber(value, stx) {
    return Syntax.from('number', value, stx);
  }

  static fromString(value, stx) {
    return Syntax.from('string', value, stx);
  }

  static fromPunctuator(value, stx) {
    return Syntax.from('punctuator', value, stx);
  }

  static fromKeyword(value, stx) {
    return Syntax.from('keyword', value, stx);
  }

  static fromIdentifier(value, stx) {
    return Syntax.from('identifier', value, stx);
  }

  static fromRegularExpression(value, stx) {
    return Syntax.from('regularExpression', value, stx);
  }

  // () -> string
  resolve(phase: any) {
    assert(phase != null, 'must provide a phase to resolve');
    let allScopes = this.scopesets.all;
    let stxScopes = this.scopesets.phase.has(phase)
      ? this.scopesets.phase.get(phase)
      : List();
    stxScopes = allScopes.concat(stxScopes);
    if (
      stxScopes.size === 0 ||
      !(this.match('identifier') ||
        this.match('keyword') ||
        this.match('punctuator'))
    ) {
      return this.token.value;
    }
    let scope = stxScopes.last();
    let bindings = this.bindings;
    if (scope) {
      // List<{ scopes: List<Scope>, binding: Symbol }>
      let scopesetBindingList = bindings.get(this);

      if (scopesetBindingList) {
        // { scopes: List<Scope>, binding: Symbol }
        let biggestBindingPair = scopesetBindingList
          .filter(({ scopes }) => {
            return scopes.isSubset(stxScopes);
          })
          .sort(sizeDecending);

        if (
          biggestBindingPair.size >= 2 &&
          biggestBindingPair.get(0).scopes.size ===
            biggestBindingPair.get(1).scopes.size
        ) {
          let debugBase =
            '{' + stxScopes.map(s => s.toString()).join(', ') + '}';
          let debugAmbigousScopesets = biggestBindingPair
            .map(({ scopes }) => {
              return '{' + scopes.map(s => s.toString()).join(', ') + '}';
            })
            .join(', ');
          throw new Error(
            'Scopeset ' +
              debugBase +
              ' has ambiguous subsets ' +
              debugAmbigousScopesets,
          );
        } else if (biggestBindingPair.size !== 0) {
          let bindingStr = biggestBindingPair.get(0).binding.toString();
          if (Maybe.isJust(biggestBindingPair.get(0).alias)) {
            // null never happens because we just checked if it is a Just
            return biggestBindingPair
              .get(0)
              .alias.getOrElse(null)
              .resolve(phase);
          }
          return bindingStr;
        }
      }
    }
    return this.token.value;
  }

  val(): any {
    assert(!this.match('delimiter'), 'cannot get the val of a delimiter');
    if (this.match('string')) {
      return this.token.str;
    }
    if (this.match('template')) {
      if (!this.token.items) return this.token.value;
      return this.token.items
        .map(el => {
          if (typeof el.match === 'function' && el.match('delimiter')) {
            return '${...}';
          }
          return el.slice.text;
        })
        .join('');
    }
    return this.token.value;
  }

  lineNumber() {
    if (!this.match('delimiter')) {
      return this.token.slice.startLocation.line;
    } else {
      return this.token.get(0).lineNumber();
    }
  }

  setLineNumber(line: number) {
    let newTok = {};
    if (this.isDelimiter()) {
      newTok = this.token.map(s => s.setLineNumber(line));
    } else {
      for (let key of Object.keys(this.token)) {
        newTok[key] = this.token[key];
      }
      assert(
        newTok.slice && newTok.slice.startLocation,
        'all tokens must have line info',
      );
      newTok.slice.startLocation.line = line;
    }
    return new Syntax(newTok, this);
  }

  // () -> List<Syntax>
  // inner() {
  //   assert(this.match("delimiter"), "can only get the inner of a delimiter");
  //   return this.token.slice(1, this.token.size - 1);
  // }

  addScope(
    scope: any,
    bindings: any,
    phase: number | {},
    options: any = { flip: false },
  ) {
    let token = this.match('delimiter')
      ? this.token.map(s => s.addScope(scope, bindings, phase, options))
      : this.token;
    if (this.match('template')) {
      token = _.merge(token, {
        items: token.items.map(it => {
          if (it instanceof Syntax && it.match('delimiter')) {
            return it.addScope(scope, bindings, phase, options);
          }
          return it;
        }),
      });
    }
    let oldScopeset;
    if (phase === ALL_PHASES) {
      oldScopeset = this.scopesets.all;
    } else {
      oldScopeset = this.scopesets.phase.has(phase)
        ? this.scopesets.phase.get(phase)
        : List();
    }
    let newScopeset;
    if (options.flip) {
      let index = oldScopeset.indexOf(scope);
      if (index !== -1) {
        newScopeset = oldScopeset.remove(index);
      } else {
        newScopeset = oldScopeset.push(scope);
      }
    } else {
      newScopeset = oldScopeset.push(scope);
    }
    let newstx = {
      bindings,
      scopesets: {
        all: this.scopesets.all,
        phase: this.scopesets.phase,
      },
    };

    if (phase === ALL_PHASES) {
      newstx.scopesets.all = newScopeset;
    } else {
      newstx.scopesets.phase = newstx.scopesets.phase.set(phase, newScopeset);
    }
    return new Syntax(token, newstx);
  }

  removeScope(scope: any, phase: number) {
    let token = this.match('delimiter')
      ? this.token.map(s => s.removeScope(scope, phase))
      : this.token;
    let phaseScopeset = this.scopesets.phase.has(phase)
      ? this.scopesets.phase.get(phase)
      : List();
    let allScopeset = this.scopesets.all;
    let newstx = {
      bindings: this.bindings,
      scopesets: {
        all: this.scopesets.all,
        phase: this.scopesets.phase,
      },
    };

    let phaseIndex = phaseScopeset.indexOf(scope);
    let allIndex = allScopeset.indexOf(scope);
    if (phaseIndex !== -1) {
      newstx.scopesets.phase = this.scopesets.phase.set(
        phase,
        phaseScopeset.remove(phaseIndex),
      );
    } else if (allIndex !== -1) {
      newstx.scopesets.all = allScopeset.remove(allIndex);
    }
    return new Syntax(token, newstx);
  }

  match(type: TokenTag, value: any) {
    if (!Types[type]) {
      throw new Error(type + ' is an invalid type');
    }
    return (
      Types[type].match(this.token) &&
      (value == null ||
        (value instanceof RegExp
          ? value.test(this.val())
          : this.val() == value))
    );
  }

  isIdentifier(value: string) {
    return this.match('identifier', value);
  }

  isAssign(value: string) {
    return this.match('assign', value);
  }

  isBooleanLiteral(value: boolean) {
    return this.match('boolean', value);
  }

  isKeyword(value: string) {
    return this.match('keyword', value);
  }

  isNullLiteral(value: any) {
    return this.match('null', value);
  }

  isNumericLiteral(value: number) {
    return this.match('number', value);
  }

  isPunctuator(value: string) {
    return this.match('punctuator', value);
  }

  isStringLiteral(value: string) {
    return this.match('string', value);
  }

  isRegularExpression(value: any) {
    return this.match('regularExpression', value);
  }

  isTemplate(value: any) {
    return this.match('template', value);
  }

  isDelimiter(value: any) {
    return this.match('delimiter', value);
  }

  isParens(value: any) {
    return this.match('parens', value);
  }

  isBraces(value: any) {
    return this.match('braces', value);
  }

  isBrackets(value: any) {
    return this.match('brackets', value);
  }

  isSyntaxTemplate(value: any) {
    return this.match('syntaxTemplate', value);
  }

  isEOF(value: any) {
    return this.match('eof', value);
  }

  toString() {
    if (this.match('delimiter')) {
      return this.token.map(s => s.toString()).join(' ');
    }
    if (this.match('string')) {
      return `'${this.token.str}'`;
    }
    if (this.match('template')) {
      return this.val();
    }
    return this.token.value;
  }
}

// @flow
import { List } from 'immutable';
import _ from 'ramda';
import { assert } from './errors';
import * as T from 'sweet-spec';
import Syntax from './syntax';

/*
Given a syntax list like:

  [foo, bar, $, { 42, +, 24 }, baz]

convert it to:

  [foo, bar, $, { 0 }, baz]

and return another list with the interpolated values at the corresponding
positions.

Requires either lookahead/lookbehind of one (to see the $).
*/

const isDolar = (s: T.SyntaxTerm) =>
  s instanceof T.RawSyntax &&
  typeof s.value.match === 'function' &&
  s.value.match('identifier') &&
  s.value.val() === '$';
const isDelimiter = (s: T.SyntaxTerm) => s instanceof T.RawDelimiter;
const isBraces = (s: T.SyntaxTerm) =>
  s instanceof T.RawDelimiter && s.kind === 'braces';
const isParens = (s: T.SyntaxTerm) =>
  s instanceof T.RawDelimiter && s.kind === 'parens';
const isBrackets = (s: T.SyntaxTerm) =>
  s instanceof T.RawDelimiter && s.kind === 'brackets';

type DelimKind = 'braces' | 'parens' | 'brackets';

const mkDelimiter = (
  kind: DelimKind,
  inner: List<T.SyntaxTerm>,
  from: T.RawDelimiter,
) => {
  return new T.RawDelimiter({
    kind,
    inner: List.of(from.inner.first()).concat(inner).concat(from.inner.last()),
  });
};

const insertIntoDelimiter = _.cond([
  [isBraces, (s, r) => mkDelimiter('braces', r, s)],
  [isParens, (s, r) => mkDelimiter('parens', r, s)],
  [isBrackets, (s, r) => mkDelimiter('brackets', r, s)],
]);

const process = (
  acc: { template: List<T.SyntaxTerm>, interp: List<List<T.SyntaxTerm>> },
  s: T.SyntaxTerm,
) => {
  if (isBraces(s) && isDolar(acc.template.last())) {
    let idx = Syntax.fromNumber(acc.interp.size, s.inner.first().value);
    return {
      template: acc.template.push(
        mkDelimiter(
          'braces',
          List.of(
            new T.RawSyntax({
              value: idx,
            }),
          ),
          s,
        ),
      ),
      interp: acc.interp.push(s.inner.slice(1, s.inner.size - 1)),
    };
  } else if (isDelimiter(s)) {
    let innerResult = processTemplate(
      s.inner.slice(1, s.inner.size - 1),
      acc.interp,
    );
    return {
      template: acc.template.push(insertIntoDelimiter(s, innerResult.template)),
      interp: innerResult.interp,
    };
  } else {
    return {
      template: acc.template.push(s),
      interp: acc.interp,
    };
  }
};

function getLineNumber(t: T.SyntaxTerm) {
  if (t instanceof T.RawSyntax) {
    return t.value.lineNumber();
  }
  return t.inner.first().value.lineNumber();
}

function setLineNumber(t: T.Term | List<T.Term>, lineNumber: number) {
  if (t instanceof T.RawSyntax) {
    return t.extend({
      value: t.value.setLineNumber(lineNumber),
    });
  } else if (t instanceof T.RawDelimiter) {
    return t.extend({
      inner: t.inner.map(tt => setLineNumber(tt, lineNumber)),
    });
  } else if (List.isList(t)) {
    return t.map(tt => setLineNumber(tt, lineNumber));
  }
  // TODO: need to handle line numbers for all AST nodes
  return t;
}

function cloneLineNumber(to: T.Term, from: T.SyntaxTerm) {
  if (from && to) {
    return setLineNumber(to, getLineNumber(from));
  }
  return to;
}

const replace = (
  acc: { template: List<T.SyntaxTerm>, rep: List<T.Term | List<T.Term>> },
  s: T.SyntaxTerm,
) => {
  let last = acc.template.get(-1);
  let beforeLast = acc.template.get(-2);
  if (isBraces(s) && isDolar(last)) {
    let index = s.inner.get(1).value.val();
    assert(acc.rep.size > index, 'unknown replacement value');
    // TODO: figure out holistic solution to line nubmers and ASI
    let replacement = cloneLineNumber(acc.rep.get(index), beforeLast);
    // let replacement = acc.rep.get(index);
    return {
      template: acc.template.pop().concat(replacement),
      rep: acc.rep,
    };
  } else if (isDelimiter(s)) {
    let innerResult = replaceTemplate(
      s.inner.slice(1, s.inner.size - 1),
      acc.rep,
    );
    return {
      template: acc.template.push(insertIntoDelimiter(s, innerResult)),
      rep: acc.rep,
    };
  } else {
    return {
      template: acc.template.push(s),
      rep: acc.rep,
    };
  }
};

export function processTemplate(
  temp: List<T.SyntaxTerm>,
  interp: List<T.SyntaxTerm> = List(),
) {
  return temp.reduce(process, { template: List(), interp });
}

export function replaceTemplate(temp: List<T.SyntaxTerm>, rep: any) {
  return temp.reduce(replace, { template: List(), rep }).template;
}

import { List } from 'immutable';
import { isExpressionStatement, isLiteralStringExpression } from './terms';
import Term, * as T from 'sweet-spec';
import { freshScope } from './scope';
import Compiler from './compiler';
import { ALL_PHASES } from './syntax';
import { Enforester } from './enforester';
import { processTemplate } from './template-processor';
import ASTDispatcher from './ast-dispatcher';
import ScopeReducer from './scope-reducer';
import { gensym } from './symbol';
import { VarBindingTransform } from './transforms';
import Syntax from './syntax';

export default class TermExpander extends ASTDispatcher {
  constructor(context) {
    super('expand', true);
    this.context = context;
  }

  expand(term) {
    return this.dispatch(term);
  }

  expandRawSyntax(term) {
    return term;
  }

  expandRawDelimiter(term) {
    return term;
  }

  expandTemplateExpression(term) {
    return new T.TemplateExpression({
      tag: term.tag == null ? null : this.expand(term.tag),
      elements: term.elements.toArray(),
    });
  }

  expandBreakStatement(term) {
    return new T.BreakStatement({
      label: term.label ? term.label.val() : null,
    });
  }

  expandDoWhileStatement(term) {
    return new T.DoWhileStatement({
      body: this.expand(term.body),
      test: this.expand(term.test),
    });
  }

  expandWithStatement(term) {
    return new T.WithStatement({
      body: this.expand(term.body),
      object: this.expand(term.object),
    });
  }

  expandDebuggerStatement(term) {
    return term;
  }

  expandContinueStatement(term) {
    return new T.ContinueStatement({
      label: term.label ? term.label.val() : null,
    });
  }

  expandSwitchStatementWithDefault(term) {
    return new T.SwitchStatementWithDefault({
      discriminant: this.expand(term.discriminant),
      preDefaultCases: term.preDefaultCases.map(c => this.expand(c)).toArray(),
      defaultCase: this.expand(term.defaultCase),
      postDefaultCases: term.postDefaultCases
        .map(c => this.expand(c))
        .toArray(),
    });
  }

  expandComputedMemberExpression(term) {
    return new T.ComputedMemberExpression({
      object: this.expand(term.object),
      expression: this.expand(term.expression),
    });
  }

  expandSwitchStatement(term) {
    return new T.SwitchStatement({
      discriminant: this.expand(term.discriminant),
      cases: term.cases.map(c => this.expand(c)).toArray(),
    });
  }

  expandFormalParameters(term) {
    let rest = term.rest == null ? null : this.expand(term.rest);
    return new T.FormalParameters({
      items: term.items.map(i => this.expand(i)),
      rest,
    });
  }

  expandArrowExpressionE(term) {
    return this.doFunctionExpansion(term, 'ArrowExpression');
  }

  expandArrowExpression(term) {
    return this.doFunctionExpansion(term, 'ArrowExpression');
  }

  expandSwitchDefault(term) {
    return new T.SwitchDefault({
      consequent: term.consequent.map(c => this.expand(c)).toArray(),
    });
  }

  expandSwitchCase(term) {
    return new T.SwitchCase({
      test: this.expand(term.test),
      consequent: term.consequent.map(c => this.expand(c)).toArray(),
    });
  }

  expandForInStatement(term) {
    return new T.ForInStatement({
      left: this.expand(term.left),
      right: this.expand(term.right),
      body: this.expand(term.body),
    });
  }

  expandTryCatchStatement(term) {
    return new T.TryCatchStatement({
      body: this.expand(term.body),
      catchClause: this.expand(term.catchClause),
    });
  }

  expandTryFinallyStatement(term) {
    let catchClause = term.catchClause == null
      ? null
      : this.expand(term.catchClause);
    return new T.TryFinallyStatement({
      body: this.expand(term.body),
      catchClause,
      finalizer: this.expand(term.finalizer),
    });
  }

  expandCatchClause(term) {
    return new T.CatchClause({
      binding: this.expand(term.binding),
      body: this.expand(term.body),
    });
  }

  expandThrowStatement(term) {
    return new T.ThrowStatement({
      expression: this.expand(term.expression),
    });
  }

  expandForOfStatement(term) {
    return new T.ForOfStatement({
      left: this.expand(term.left),
      right: this.expand(term.right),
      body: this.expand(term.body),
    });
  }

  expandBindingIdentifier(term) {
    return term;
  }

  expandBindingPropertyIdentifier(term) {
    return term;
  }
  expandBindingPropertyProperty(term) {
    return new T.BindingPropertyProperty({
      name: this.expand(term.name),
      binding: this.expand(term.binding),
    });
  }

  expandComputedPropertyName(term) {
    return new T.ComputedPropertyName({
      expression: this.expand(term.expression),
    });
  }

  expandObjectBinding(term) {
    return new T.ObjectBinding({
      properties: term.properties.map(t => this.expand(t)).toArray(),
    });
  }

  expandArrayBinding(term) {
    let restElement = term.restElement == null
      ? null
      : this.expand(term.restElement);
    return new T.ArrayBinding({
      elements: term.elements
        .map(t => (t == null ? null : this.expand(t)))
        .toArray(),
      restElement,
    });
  }

  expandBindingWithDefault(term) {
    return new T.BindingWithDefault({
      binding: this.expand(term.binding),
      init: this.expand(term.init),
    });
  }

  expandShorthandProperty(term) {
    // because hygiene, shorthand properties must turn into DataProperties
    return new T.DataProperty({
      name: new T.StaticPropertyName({
        value: term.name,
      }),
      expression: new T.IdentifierExpression({
        name: term.name,
      }),
    });
  }

  expandForStatement(term) {
    let init = term.init == null ? null : this.expand(term.init);
    let test = term.test == null ? null : this.expand(term.test);
    let update = term.update == null ? null : this.expand(term.update);
    let body = this.expand(term.body);
    return new T.ForStatement({ init, test, update, body });
  }

  expandYieldExpression(term) {
    let expr = term.expression == null ? null : this.expand(term.expression);
    return new T.YieldExpression({
      expression: expr,
    });
  }

  expandYieldGeneratorExpression(term) {
    let expr = term.expression == null ? null : this.expand(term.expression);
    return new T.YieldGeneratorExpression({
      expression: expr,
    });
  }

  expandWhileStatement(term) {
    return new T.WhileStatement({
      test: this.expand(term.test),
      body: this.expand(term.body),
    });
  }

  expandIfStatement(term) {
    let consequent = term.consequent == null
      ? null
      : this.expand(term.consequent);
    let alternate = term.alternate == null ? null : this.expand(term.alternate);
    return new T.IfStatement({
      test: this.expand(term.test),
      consequent: consequent,
      alternate: alternate,
    });
  }

  expandBlockStatement(term) {
    return new T.BlockStatement({
      block: this.expand(term.block),
    });
  }

  expandBlock(term) {
    let scope = freshScope('block');
    this.context.currentScope.push(scope);
    let compiler = new Compiler(
      this.context.phase,
      this.context.env,
      this.context.store,
      this.context,
    );

    let markedBody, bodyTerm;
    markedBody = term.statements.map(b =>
      b.reduce(
        new ScopeReducer(
          [{ scope, phase: ALL_PHASES, flip: false }],
          this.context.bindings,
        ),
      ),
    );
    bodyTerm = new T.Block({
      statements: compiler.compile(markedBody),
    });
    this.context.currentScope.pop();
    return bodyTerm;
  }

  expandVariableDeclarationStatement(term) {
    return new T.VariableDeclarationStatement({
      declaration: this.expand(term.declaration),
    });
  }
  expandReturnStatement(term) {
    if (term.expression == null) {
      return term;
    }
    return new T.ReturnStatement({
      expression: this.expand(term.expression),
    });
  }

  expandClassDeclaration(term) {
    return new T.ClassDeclaration({
      name: term.name == null ? null : this.expand(term.name),
      super: term.super == null ? null : this.expand(term.super),
      elements: term.elements.map(el => this.expand(el)).toArray(),
    });
  }

  expandClassExpression(term) {
    return new T.ClassExpression({
      name: term.name == null ? null : this.expand(term.name),
      super: term.super == null ? null : this.expand(term.super),
      elements: term.elements.map(el => this.expand(el)).toArray(),
    });
  }

  expandClassElement(term) {
    return new T.ClassElement({
      isStatic: term.isStatic,
      method: this.expand(term.method),
    });
  }

  expandThisExpression(term) {
    return term;
  }

  expandSyntaxTemplate(term) {
    let r = processTemplate(term.template.slice(1, term.template.size - 1));
    let ident = this.context.getTemplateIdentifier();
    this.context.templateMap.set(ident, r.template);
    let name = Syntax.fromIdentifier(
      'syntaxTemplate',
      term.template.first().value,
    );
    let callee = new T.IdentifierExpression({
      name: name,
    });

    let expandedInterps = r.interp.map(i => {
      let enf = new Enforester(i, List(), this.context);
      return this.expand(enf.enforest('expression'));
    });

    let args = List.of(new T.LiteralNumericExpression({ value: ident })).concat(
      expandedInterps,
    );

    return new T.CallExpression({
      callee,
      arguments: args,
    });
  }

  expandStaticMemberExpression(term) {
    return new T.StaticMemberExpression({
      object: this.expand(term.object),
      property: term.property,
    });
  }

  expandArrayExpression(term) {
    return new T.ArrayExpression({
      elements: term.elements.map(t => (t == null ? t : this.expand(t))),
    });
  }

  expandImport(term) {
    return term;
  }

  expandImportNamespace(term) {
    return term;
  }

  expandExport(term) {
    return new T.Export({
      declaration: this.expand(term.declaration),
    });
  }

  expandExportDefault(term) {
    return new T.ExportDefault({
      body: this.expand(term.body),
    });
  }

  expandExportFrom(term) {
    return term;
  }

  expandExportAllFrom(term) {
    return term;
  }

  expandExportSpecifier(term) {
    return term;
  }

  expandStaticPropertyName(term) {
    return term;
  }

  expandDataProperty(term) {
    return new T.DataProperty({
      name: this.expand(term.name),
      expression: this.expand(term.expression),
    });
  }

  expandObjectExpression(term) {
    return new T.ObjectExpression({
      properties: term.properties.map(t => this.expand(t)),
    });
  }

  expandVariableDeclarator(term) {
    let init = term.init == null ? null : this.expand(term.init);
    return new T.VariableDeclarator({
      binding: this.expand(term.binding),
      init: init,
    });
  }

  expandVariableDeclaration(term) {
    if (
      term.kind === 'syntax' ||
      term.kind === 'syntaxrec' ||
      term.kind === 'operator'
    ) {
      return term;
    }
    return new T.VariableDeclaration({
      kind: term.kind,
      declarators: term.declarators.map(d => this.expand(d)),
    });
  }

  expandParenthesizedExpression(term) {
    if (term.inner.size === 0) {
      throw new Error('unexpected end of input');
    }
    let enf = new Enforester(term.inner, List(), this.context);
    let lookahead = enf.peek();
    let t = enf.enforestExpression();
    if (t == null || enf.rest.size > 0) {
      if (enf.rest.size === 0) {
        throw enf.createError(')', 'unexpected token');
      }
      throw enf.createError(lookahead, 'unexpected syntax');
    }
    return this.expand(t);
  }

  expandUnaryExpression(term) {
    return new T.UnaryExpression({
      operator: term.operator,
      operand: this.expand(term.operand),
    });
  }

  expandUpdateExpression(term) {
    return new T.UpdateExpression({
      isPrefix: term.isPrefix,
      operator: term.operator,
      operand: this.expand(term.operand),
    });
  }

  expandBinaryExpression(term) {
    let left = this.expand(term.left);
    let right = this.expand(term.right);
    return new T.BinaryExpression({
      left: left,
      operator: term.operator,
      right: right,
    });
  }

  expandConditionalExpression(term) {
    return new T.ConditionalExpression({
      test: this.expand(term.test),
      consequent: this.expand(term.consequent),
      alternate: this.expand(term.alternate),
    });
  }

  expandNewTargetExpression(term) {
    return term;
  }

  expandNewExpression(term) {
    let callee = this.expand(term.callee);
    let enf = new Enforester(term.arguments, List(), this.context);
    let args = enf.enforestArgumentList().map(arg => this.expand(arg));
    return new T.NewExpression({
      callee,
      arguments: args.toArray(),
    });
  }

  expandSuper(term) {
    return term;
  }

  expandCallExpressionE(term) {
    let callee = this.expand(term.callee);
    let enf = new Enforester(term.arguments, List(), this.context);
    let args = enf.enforestArgumentList().map(arg => this.expand(arg));
    return new T.CallExpression({
      callee: callee,
      arguments: args,
    });
  }

  expandSpreadElement(term) {
    return new T.SpreadElement({
      expression: this.expand(term.expression),
    });
  }

  expandExpressionStatement(term) {
    let child = this.expand(term.expression);
    return new T.ExpressionStatement({
      expression: child,
    });
  }

  expandLabeledStatement(term) {
    return new T.LabeledStatement({
      label: term.label.val(),
      body: this.expand(term.body),
    });
  }

  doFunctionExpansion(term, type) {
    let scope = freshScope('fun');
    let params;
    let self = this;
    if (type !== 'Getter' && type !== 'Setter') {
      // TODO: need to register the parameter bindings again
      params = term.params.reduce(
        new class extends Term.CloneReducer {
          reduceBindingIdentifier(term) {
            let name = term.name.addScope(
              scope,
              self.context.bindings,
              ALL_PHASES,
            );
            let newBinding = gensym(name.val());

            self.context.env.set(
              newBinding.toString(),
              new VarBindingTransform(name),
            );
            self.context.bindings.add(name, {
              binding: newBinding,
              phase: self.context.phase,
              skipDup: true,
            });
            return new T.BindingIdentifier({ name });
          }
        }(),
      );
      params = this.expand(params);
    }
    this.context.currentScope.push(scope);
    let compiler = new Compiler(
      this.context.phase,
      this.context.env,
      this.context.store,
      this.context,
    );

    let bodyTerm;
    let scopeReducer = new ScopeReducer(
      [{ scope, phase: ALL_PHASES, flip: false }],
      this.context.bindings,
    );
    if (term.body instanceof Term) {
      // Arrow functions have a single term as their body
      bodyTerm = this.expand(term.body.reduce(scopeReducer));
    } else {
      let compiledBody = compiler.compile(
        term.body.map(b => b.reduce(scopeReducer)),
      );
      const directives = compiledBody
        .takeWhile(
          s =>
            isExpressionStatement(s) && isLiteralStringExpression(s.expression),
        )
        .map(s => new T.Directive({ rawValue: s.expression.value }));
      bodyTerm = new T.FunctionBody({
        directives: directives,
        statements: compiledBody.slice(directives.size),
      });
    }
    this.context.currentScope.pop();

    switch (type) {
      case 'Getter':
        return new T.Getter({
          name: this.expand(term.name),
          body: bodyTerm,
        });
      case 'Setter':
        return new T.Setter({
          name: this.expand(term.name),
          param: term.param,
          body: bodyTerm,
        });
      case 'Method':
        return new T.Method({
          name: term.name,
          isGenerator: term.isGenerator,
          params: params,
          body: bodyTerm,
        });
      case 'ArrowExpression':
        return new T.ArrowExpression({
          params: params,
          body: bodyTerm,
        });
      case 'FunctionExpression':
        return new T.FunctionExpression({
          name: term.name,
          isGenerator: term.isGenerator,
          params: params,
          body: bodyTerm,
        });
      case 'FunctionDeclaration':
        return new T.FunctionDeclaration({
          name: term.name,
          isGenerator: term.isGenerator,
          params: params,
          body: bodyTerm,
        });
      default:
        throw new Error(`Unknown function type: ${type}`);
    }
  }

  expandMethod(term) {
    return this.doFunctionExpansion(term, 'Method');
  }

  expandSetter(term) {
    return this.doFunctionExpansion(term, 'Setter');
  }

  expandGetter(term) {
    return this.doFunctionExpansion(term, 'Getter');
  }

  expandFunctionDeclarationE(term) {
    return this.doFunctionExpansion(term, 'FunctionDeclaration');
  }

  expandFunctionExpressionE(term) {
    return this.doFunctionExpansion(term, 'FunctionExpression');
  }

  expandCompoundAssignmentExpression(term) {
    return new T.CompoundAssignmentExpression({
      binding: this.expand(term.binding),
      operator: term.operator,
      expression: this.expand(term.expression),
    });
  }

  expandAssignmentExpression(term) {
    return new T.AssignmentExpression({
      binding: this.expand(term.binding),
      expression: this.expand(term.expression),
    });
  }

  expandEmptyStatement(term) {
    return term;
  }

  expandLiteralBooleanExpression(term) {
    return term;
  }

  expandLiteralNumericExpression(term) {
    return term;
  }
  expandLiteralInfinityExpression(term) {
    return term;
  }

  expandIdentifierExpression(term) {
    let trans = this.context.env.get(term.name.resolve(this.context.phase));
    if (trans) {
      return new T.IdentifierExpression({
        name: trans.id,
      });
    }
    return term;
  }

  expandLiteralNullExpression(term) {
    return term;
  }

  expandLiteralStringExpression(term) {
    return term;
  }

  expandLiteralRegExpExpression(term) {
    return term;
  }
}

import * as R from 'ramda';
import Term from 'sweet-spec';

// bindings
export const isBindingWithDefault = R.whereEq({ type: 'BindingWithDefault' });
export const isBindingIdentifier = R.whereEq({ type: 'BindingIdentifier' });
export const isArrayBinding = R.whereEq({ type: 'ArrayBinding' });
export const isObjectBinding = R.whereEq({ type: 'ObjectBinding' });
export const isBindingPropertyIdentifier = R.whereEq({
  type: 'BindingPropertyIdentifier',
});
export const isBindingPropertyProperty = R.whereEq({
  type: 'BindingPropertyIdentifier',
});

// class
export const isClassExpression = R.whereEq({ type: 'ClassExpression' });
export const isClassDeclaration = R.whereEq({ type: 'ClassDeclaration' });
export const isClassElement = R.whereEq({ type: 'ClassElement' });

// modules
export const isModule = R.whereEq({ type: 'Module' });
export const isImport = R.whereEq({ type: 'Import' });
export const isImportNamespace = R.whereEq({ type: 'ImportNamespace' });
export const isImportSpecifier = R.whereEq({ type: 'ImportSpecifier' });
export const isExportAllFrom = R.whereEq({ type: 'ExportAllFrom' });
export const isExportFrom = R.whereEq({ type: 'ExportFrom' });
export const isExport = R.whereEq({ type: 'Export' });
export const isExportDefault = R.whereEq({ type: 'ExportDefault' });
export const isExportSpecifier = R.whereEq({ type: 'ExportSpecifier' });

// property definition
export const isMethod = R.whereEq({ type: 'Method' });
export const isGetter = R.whereEq({ type: 'Getter' });
export const isSetter = R.whereEq({ type: 'Setter' });
export const isDataProperty = R.whereEq({ type: 'DataProperty' });
export const isShorthandProperty = R.whereEq({ type: 'ShorthandProperty' });
export const isComputedPropertyName = R.whereEq({
  type: 'ComputedPropertyName',
});
export const isStaticPropertyName = R.whereEq({ type: 'StaticPropertyName' });

// literals
export const isLiteralBooleanExpression = R.whereEq({
  type: 'LiteralBooleanExpression',
});
export const isLiteralInfinityExpression = R.whereEq({
  type: 'LiteralInfinityExpression',
});
export const isLiteralNullExpression = R.whereEq({
  type: 'LiteralNullExpression',
});
export const isLiteralNumericExpression = R.whereEq({
  type: 'LiteralNumericExpression',
});
export const isLiteralRegExpExpression = R.whereEq({
  type: 'LiteralRegExpExpression',
});
export const isLiteralStringExpression = R.whereEq({
  type: 'LiteralStringExpression',
});

// expressions
export const isArrayExpression = R.whereEq({ type: 'ArrayExpression' });
export const isArrowExpression = R.whereEq({ type: 'ArrowExpression' });
export const isAssignmentExpression = R.whereEq({
  type: 'AssignmentExpression',
});
export const isBinaryExpression = R.whereEq({ type: 'BinaryExpression' });
export const isCallExpression = R.whereEq({ type: 'CallExpression' });
export const isComputedAssignmentExpression = R.whereEq({
  type: 'ComputedAssignmentExpression',
});
export const isComputedMemberExpression = R.whereEq({
  type: 'ComputedMemberExpression',
});
export const isConditionalExpression = R.whereEq({
  type: 'ConditionalExpression',
});
export const isFunctionExpression = R.whereEq({ type: 'FunctionExpression' });
export const isIdentifierExpression = R.whereEq({
  type: 'IdentifierExpression',
});
export const isNewExpression = R.whereEq({ type: 'NewExpression' });
export const isNewTargetExpression = R.whereEq({ type: 'NewTargetExpression' });
export const isObjectExpression = R.whereEq({ type: 'ObjectExpression' });
export const isUnaryExpression = R.whereEq({ type: 'UnaryExpression' });
export const isStaticMemberExpression = R.whereEq({
  type: 'StaticMemberExpression',
});
export const isTemplateExpression = R.whereEq({ type: 'TemplateExpression' });
export const isThisExpression = R.whereEq({ type: 'ThisExpression' });
export const isUpdateExpression = R.whereEq({ type: 'UpdateExpression' });
export const isYieldExpression = R.whereEq({ type: 'YieldExpression' });
export const isYieldGeneratorExpression = R.whereEq({
  type: 'YieldGeneratorExpression',
});

// statements
export const isBlockStatement = R.whereEq({ type: 'BlockStatement' });
export const isBreakStatement = R.whereEq({ type: 'BreakStatement' });
export const isContinueStatement = R.whereEq({ type: 'ContinueStatement' });
export const isCompoundAssignmentExpression = R.whereEq({
  type: 'CompoundAssignmentExpression',
});
export const isDebuggerStatement = R.whereEq({ type: 'DebuggerStatement' });
export const isDoWhileStatement = R.whereEq({ type: 'DoWhileStatement' });
export const isEmptyStatement = R.whereEq({ type: 'EmptyStatement' });
export const isExpressionStatement = R.whereEq({ type: 'ExpressionStatement' });
export const isForInStatement = R.whereEq({ type: 'ForInStatement' });
export const isForOfStatement = R.whereEq({ type: 'ForOfStatement' });
export const isForStatement = R.whereEq({ type: 'ForStatement' });
export const isIfStatement = R.whereEq({ type: 'IfStatement' });
export const isLabeledStatement = R.whereEq({ type: 'LabeledStatement' });
export const isReturnStatement = R.whereEq({ type: 'ReturnStatement' });
export const isSwitchStatement = R.whereEq({ type: 'SwitchStatement' });
export const isSwitchStatementWithDefault = R.whereEq({
  type: 'SwitchStatementWithDefault',
});
export const isThrowStatement = R.whereEq({ type: 'ThrowStatement' });
export const isTryCatchStatement = R.whereEq({ type: 'TryCatchStatement' });
export const isTryFinallyStatement = R.whereEq({ type: 'TryFinallyStatement' });
export const isVariableDeclarationStatement = R.whereEq({
  type: 'VariableDeclarationStatement',
});
export const isWhileStatement = R.whereEq({ type: 'WhileStatement' });
export const isWithStatement = R.whereEq({ type: 'WithStatement' });

// other
export const isBlock = R.whereEq({ type: 'Block' });
export const isCatchClause = R.whereEq({ type: 'CatchClause' });
export const isDirective = R.whereEq({ type: 'Directive' });
export const isFormalParameters = R.whereEq({ type: 'FormalParameters' });
export const isFunctionBody = R.whereEq({ type: 'FunctionBody' });
export const isFunctionDeclaration = R.whereEq({ type: 'FunctionDeclaration' });
export const isScript = R.whereEq({ type: 'Script' });
export const isSpreadElement = R.whereEq({ type: 'SpreadElement' });
export const isSuper = R.whereEq({ type: 'Super' });
export const isSwitchCase = R.whereEq({ type: 'SwitchCase' });
export const isSwitchDefault = R.whereEq({ type: 'SwitchDefault' });
export const isTemplateElement = R.whereEq({ type: 'TemplateElement' });
export const isSyntaxTemplate = R.whereEq({ type: 'SyntaxTemplate' });
export const isVariableDeclaration = R.whereEq({ type: 'VariableDeclaration' });
export const isVariableDeclarator = R.whereEq({ type: 'VariableDeclarator' });
export const isEOF = R.whereEq({ type: 'EOF' });
export const isSyntaxDeclaration = R.both(
  isVariableDeclaration,
  R.whereEq({ kind: 'syntax' }),
);
export const isSyntaxrecDeclaration = R.both(
  isVariableDeclaration,
  R.whereEq({ kind: 'syntaxrec' }),
);
export const isFunctionTerm = R.either(
  isFunctionDeclaration,
  isFunctionExpression,
);
export const isFunctionWithName = R.and(
  isFunctionTerm,
  R.complement(R.where({ name: R.isNil })),
);
export const isParenthesizedExpression = R.whereEq({
  type: 'ParenthesizedExpression',
});
export const isExportSyntax = R.both(isExport, exp =>
  R.or(
    isSyntaxDeclaration(exp.declaration),
    isSyntaxrecDeclaration(exp.declaration),
  ),
);
export const isSyntaxDeclarationStatement = R.both(
  isVariableDeclarationStatement,
  decl => isCompiletimeDeclaration(decl.declaration),
);

export const isCompiletimeDeclaration = R.either(
  isSyntaxDeclaration,
  isSyntaxrecDeclaration,
);
export const isCompiletimeStatement = term => {
  return (
    term instanceof Term &&
    isVariableDeclarationStatement(term) &&
    isCompiletimeDeclaration(term.declaration)
  );
};
export const isImportDeclaration = R.either(isImport, isImportNamespace);
export const isExportDeclaration = R.either(
  isExport,
  isExportDefault,
  isExportFrom,
  isExportAllFrom,
);

// @flow
import Term, * as S from 'sweet-spec';
import { List } from 'immutable';
import { Enforester } from './enforester';
import TermExpander from './term-expander.js';
import Env from './env';
import * as _ from 'ramda';
import * as T from './terms';
import { gensym } from './symbol';
import { VarBindingTransform, CompiletimeTransform } from './transforms';
import { evalCompiletimeValue } from './load-syntax';
import { freshScope } from './scope';
import { ALL_PHASES } from './syntax';
import ASTDispatcher from './ast-dispatcher';
import Syntax from './syntax.js';
import ScopeReducer from './scope-reducer';
import ModuleVisitor, { bindImports } from './module-visitor';

class RegisterBindingsReducer extends Term.CloneReducer {
  useScope: any;
  phase: number;
  bindings: any;
  skipDup: boolean;
  env: Env;

  constructor(
    useScope: any,
    phase: number,
    skipDup: boolean,
    bindings: any,
    env: Env,
  ) {
    super();
    this.useScope = useScope;
    this.phase = phase;
    this.bindings = bindings;
    this.skipDup = skipDup;
    this.env = env;
  }

  reduceBindingIdentifier(t: Term, s: { name: Syntax }) {
    let newName = s.name.removeScope(this.useScope, this.phase);
    let newBinding = gensym(newName.val());
    this.bindings.add(newName, {
      binding: newBinding,
      phase: this.phase,
      skipDup: this.skipDup,
    });
    this.env.set(newBinding.toString(), new VarBindingTransform(newName));
    return t.extend({
      name: newName,
    });
  }
}

class RegisterSyntaxBindingsReducer extends Term.CloneReducer {
  useScope: any;
  phase: number;
  bindings: any;
  env: Env;
  val: any;

  constructor(useScope: any, phase: number, bindings: any, env: Env, val: any) {
    super();
    this.useScope = useScope;
    this.phase = phase;
    this.bindings = bindings;
    this.env = env;
    this.val = val;
  }

  reduceBindingIdentifier(t: Term, s: { name: Syntax }) {
    let newName = s.name.removeScope(this.useScope, this.phase);
    let newBinding = gensym(newName.val());
    this.bindings.add(newName, {
      binding: newBinding,
      phase: this.phase,
      skipDup: false,
    });
    let resolvedName = newName.resolve(this.phase);
    this.env.set(resolvedName, new CompiletimeTransform(this.val));
    return t.extend({
      name: newName,
    });
  }
}

export default class TokenExpander extends ASTDispatcher {
  constructor(context: any) {
    super('expand', false);
    this.context = context;
  }

  expand(stxl: List<Syntax>) {
    let result = [];
    if (stxl.size === 0) {
      return List(result);
    }
    let prev = List();
    let enf = new Enforester(stxl, prev, this.context);

    while (!enf.done) {
      result.push(this.dispatch(enf.enforest()));
    }

    return List(result);
  }

  expandVariableDeclarationStatement(term: S.VariableDeclarationStatement) {
    return term.extend({
      declaration: this.registerVariableDeclaration(term.declaration),
    });
  }

  expandFunctionDeclaration(term: Term) {
    return this.registerFunctionOrClass(term);
  }

  // TODO: think about function expressions

  registerImport(term: S.Import | S.ImportNamespace) {
    let path = term.moduleSpecifier.val();
    let mod;
    let visitor = new ModuleVisitor(this.context);
    if (term.forSyntax) {
      mod = this.context.loader.get(
        path,
        this.context.phase + 1,
        this.context.cwd,
      );
      this.context.store = visitor.visit(
        mod,
        this.context.phase + 1,
        this.context.store,
      );
      this.context.store = visitor.invoke(
        mod,
        this.context.phase + 1,
        this.context.store,
      );
    } else {
      mod = this.context.loader.get(path, this.context.phase, this.context.cwd);
      this.context.store = visitor.visit(
        mod,
        this.context.phase,
        this.context.store,
      );
    }
    bindImports(term, mod, this.context.phase, this.context);
    return term;
  }

  expandImport(term: S.Import) {
    return this.registerImport(term);
  }

  expandImportNamespace(term: S.ImportNamespace) {
    return this.registerImport(term);
  }

  expandExport(term: Term) {
    if (
      T.isFunctionDeclaration(term.declaration) ||
      T.isClassDeclaration(term.declaration)
    ) {
      return term.extend({
        declaration: this.registerFunctionOrClass(term.declaration),
      });
    } else if (T.isVariableDeclaration(term.declaration)) {
      return term.extend({
        declaration: this.registerVariableDeclaration(term.declaration),
      });
    }
    return term;
  }

  registerFunctionOrClass(term: Term) {
    let red = new RegisterBindingsReducer(
      this.context.useScope,
      this.context.phase,
      false,
      this.context.bindings,
      this.context.env,
    );
    return term.extend({
      name: term.name.reduce(red),
    });
  }

  registerVariableDeclaration(term: Term) {
    if (
      term.kind === 'syntax' ||
      term.kind === 'syntaxrec' ||
      term.kind === 'operator'
    ) {
      return this.registerSyntaxDeclaration(term);
    }
    let red = new RegisterBindingsReducer(
      this.context.useScope,
      this.context.phase,
      term.kind === 'var',
      this.context.bindings,
      this.context.env,
    );
    return term.extend({
      declarators: term.declarators.map(decl => {
        return decl.extend({
          binding: decl.binding.reduce(red),
        });
      }),
    });
  }

  registerSyntaxDeclaration(term: Term) {
    if (term.kind === 'syntax' || term.kind === 'operator') {
      // syntax id^{a, b} = <init>^{a, b}
      // ->
      // syntaxrec id^{a,b,c} = function() { return <<id^{a}>> }
      // syntaxrec id^{a,b} = <init>^{a,b,c}
      let scope = freshScope('nonrec');
      let scopeReducer = new ScopeReducer(
        [{ scope: scope, phase: ALL_PHASES, flip: false }],
        this.context.bindings,
      );
      term = term.extend({
        declarators: term.declarators.map(decl => {
          let name = decl.binding.name;
          let nameAdded = name.addScope(
            scope,
            this.context.bindings,
            ALL_PHASES,
          );
          let nameRemoved = name.removeScope(
            this.context.currentScope[this.context.currentScope.length - 1],
            this.context.phase,
          );
          let newBinding = gensym(name.val());
          this.context.bindings.addForward(
            nameAdded,
            nameRemoved,
            newBinding,
            this.context.phase,
          );
          return decl.extend({
            init: decl.init.reduce(scopeReducer),
          });
        }),
      });
    }
    // for syntax declarations we need to load the compiletime value
    // into the environment
    let compiletimeType = term.kind === 'operator' ? 'operator' : 'syntax';
    return term.extend({
      declarators: term.declarators.map(decl => {
        // each compiletime value needs to be expanded with a fresh
        // environment and in the next higher phase
        let syntaxExpander = new TermExpander(
          _.merge(this.context, {
            phase: this.context.phase + 1,
            env: new Env(),
            store: this.context.store,
          }),
        );

        let init = syntaxExpander.expand(decl.init);
        let val = evalCompiletimeValue(
          init,
          _.merge(this.context, {
            phase: this.context.phase + 1,
          }),
        );
        let red = new RegisterSyntaxBindingsReducer(
          this.context.useScope,
          this.context.phase,
          this.context.bindings,
          this.context.env,
          {
            type: compiletimeType,
            prec: decl.prec == null ? void 0 : decl.prec.val(),
            assoc: decl.assoc == null ? void 0 : decl.assoc.val(),
            f: val,
          },
        );
        return decl.extend({ binding: decl.binding.reduce(red), init });
      }),
    });
  }

  // registerSyntaxDeclarator(term) {
  //
  // }
}

