    function findSymbol(cm, repeat, forward, symb) {
      var cur = cm.getCursor();
      var increment = forward ? 1 : -1;
      var endLine = forward ? cm.lineCount() : -1;
      var curCh = cur.ch;
      var line = cur.line;
      var lineText = cm.getLine(line);
      var state = {
