  const currentReadtable = getCurrentReadtable();
  setCurrentReadtable(primitiveReadtable);

  let results = List.of(this.readToken(stream, List(), b));

  setCurrentReadtable(currentReadtable);
  return this.readUntil(closing, stream, results, b);
}

const delimiterEntries = delimiterPairs.map(p => ({
  key: p[0],
  mode: 'terminating',
  action: function readDefaultDelimiters(stream, prefix, b) {
    return readDelimiters.call(this, p[1], stream, prefix, true);
  },
}));

const bracesEntry = {
  key: '{',
  mode: 'terminating',
  action: function readBraces(stream, prefix, b) {
    const line = this.locationInfo.line;
    const innerB = isExprPrefix(line, b, prefix);
    return readDelimiters.call(this, '}', stream, prefix, innerB);
  },
};

function readClosingDelimiter(opening, closing, stream, prefix, b) {
  if (prefix.first().value !== opening) {
    throw Error('Unmatched delimiter:', closing);
  }
  return readPunctuator.call(this, stream);
}

const unmatchedDelimiterEntries = [
  ['{', '}'],
  ['[', ']'],
  ['(', ')'],
].map(p => ({
  key: p[1],
  mode: 'terminating',
  action: function readClosingDelimiters(stream, prefix, b) {
    return readClosingDelimiter.call(this, ...p, stream, prefix, b);
  },
}));

const divEntry = {
  key: '/',
  mode: 'terminating',
  action: function readDiv(stream, prefix, b) {
    let nxt = stream.peek(1);
    if (nxt === '/' || nxt === '*') {
      const result = readComment.call(this, stream);
      return result;
    }
    if (isRegexPrefix(b, prefix)) {
      return readRegExp.call(this, stream, prefix, b);
    }
    return readPunctuator.call(this, stream);
  },
};

const dispatchBacktickEntry = {
  key: '`',
  mode: 'dispatch',
  action: readSyntaxTemplate,
};

const defaultDispatchEntry = {
  mode: 'dispatch',
  action: function readDefaultDispatch(...args) {
    this.readToken(...args);
    return EmptyToken;
  },
};

const dispatchWhiteSpaceEntries = whiteSpaceTable
  .concat(lineTerminatorTable)
  .map(w => ({
    key: w,
    mode: 'dispatch',
    action: function readDispatchWhitespace(
      stream,
      prefix,
      allowExprs,
      dispatchKey,
    ) {
      this.readToken(stream, prefix, allowExprs);
      return new IdentifierToken({ value: dispatchKey });
    },
  }));

const atEntry = {
  key: '@',
  mode: 'terminating',
  action: function readAt(stream, prefix) {
    const nxt = stream.peek(1), nxtCode = nxt.charCodeAt(0);
    if (isEOS(nxt) || isWhiteSpace(nxtCode) || isLineTerminator(nxtCode)) {
      return new IdentifierToken({ value: stream.readString() });
    }
    throw new SyntaxError('Invalid or unexpected token');
  },
};

const defaultReadtable = primitiveReadtable.extend(
  ...[
    dotEntry,
    ...delimiterEntries,
    ...unmatchedDelimiterEntries,
    bracesEntry,
    divEntry,
    ...keywordEntries,
    defaultDispatchEntry,
    dispatchBacktickEntry,
    ...dispatchWhiteSpaceEntries,
    atEntry,
  ],
);

export default defaultReadtable;

// @flow
import type { CharStream } from 'readtable';

import { isEOS } from 'readtable';
import { skipSingleLineComment } from './utils';
import { EmptyToken } from '../tokens';

export default function readComment(stream: CharStream): typeof EmptyToken {
  let char = stream.peek();

  while (!isEOS(char)) {
    let chCode = char.charCodeAt(0);
    if (chCode === 47) {
      /* "/" */ const nxt = stream.peek(1);
      if (isEOS(nxt)) {
        break;
      }
      chCode = nxt.charCodeAt(0);
      if (chCode === 47) {
        /* "/" */ skipSingleLineComment.call(this, stream);
      } else if (chCode === 42) {
        /* "*" */ skipMultiLineComment.call(this, stream);
      } else {
        break;
      }
    } else {
      break;
    }
    char = stream.peek();
  }

  return EmptyToken;
}

function skipMultiLineComment(stream: CharStream): void {
  let idx = 2;
  let char = stream.peek(idx);
  const { position: startPosition } = stream.sourceInfo;
  let lineStart;
  while (!isEOS(char)) {
    let chCode = char.charCodeAt(0);
    if (chCode < 0x80) {
      switch (chCode) {
        case 42: // "*"
          // Block comment ends with "*/".
          if (stream.peek(idx + 1).charAt(0) === '/') {
            stream.readString(idx + 2);
            if (lineStart)
              this.locationInfo.column = stream.sourceInfo.position - lineStart;
            return;
          }
          ++idx;
          break;
        case 10: // "\n"
          this.incrementLine();
          lineStart = startPosition + idx;
          ++idx;
          break;
        case 13: {
          // "\r":
          let startIdx = idx;
          if (stream.peek(idx + 1).charAt(0) === '\n') {
            ++idx;
          }
          ++idx;
          this.incrementLine();
          lineStart = startPosition + startIdx;
          break;
        }
        default:
          ++idx;
      }
    } else if (chCode === 0x2028 || chCode === 0x2029) {
      this.incrementLine();
      lineStart = startPosition + idx;
      ++idx;
    } else {
      ++idx;
    }
    char = stream.peek(idx);
  }
  throw this.createILLEGAL(char);
}

// @flow

import { getCurrentReadtable, setCurrentReadtable } from 'readtable';
import { List } from 'immutable';
import { TokenType as TT } from '../tokens';

import type { CharStream } from 'readtable';

const backtickEntry = {
  key: '`',
  mode: 'terminating',
  action: function readBacktick(
    stream: CharStream,
    prefix: List<any>,
    e: boolean,
  ) {
    if (prefix.isEmpty()) {
      return {
        type: TT.LSYNTAX,
        value: stream.readString(),
      };
    }

    return {
      type: TT.RSYNTAX,
      value: stream.readString(),
    };
  },
};

export function readSyntaxTemplate(
  stream: CharStream,
  prefix: List<any>,
  exprAllowed: boolean,
  dispatchChar: string,
): List<any> | { type: typeof TT.RSYNTAX, value: string } {
  // return read('syntaxTemplate').first().token;
  // TODO: Can we simply tack 'syntaxTemplate' on the front and process it as a
  //       syntax macro?
  const prevTable = getCurrentReadtable();
  setCurrentReadtable(prevTable.extend(backtickEntry));

  const result = this.readUntil(
    '`',
    stream,
    List.of(
      updateSyntax(dispatchChar, this.readToken(stream, List(), exprAllowed)),
    ),
    exprAllowed,
  );

  setCurrentReadtable(prevTable);
  return result;
}

function updateSyntax(prefix, token) {
  token.value = prefix + token.value;
  token.slice.text = prefix + token.slice.text;
  token.slice.start -= 1;
  token.slice.startLocation.position -= 1;
  return token;
}

// @flow

import { scanUnicode } from './utils';

import { isEOS, getCurrentReadtable } from 'readtable';
import type { CharStream } from 'readtable';

import { IdentifierToken } from '../tokens';

import { isTerminating, isIdentifierPart, isIdentifierStart } from './utils';

let terminates;

export default function readIdentifier(stream: CharStream) {
  terminates = isTerminating(getCurrentReadtable());
  let char = stream.peek();
  let code;
  let check = isIdentifierStart;
  let idx = 0;
  while (!terminates(char) && !isEOS(char)) {
    code = char.charCodeAt(0);
    if (char === '\\' || (0xd800 <= code && code <= 0xdbff)) {
      return new IdentifierToken({
        value: getEscapedIdentifier.call(this, stream),
      });
    }
    if (!check(code)) {
      return new IdentifierToken({
        value: stream.readString(idx),
      });
    }
    char = stream.peek(++idx);
    check = isIdentifierPart;
  }
  return new IdentifierToken({
    value: stream.readString(idx),
  });
}

function getEscapedIdentifier(stream) {
  const sPeek = stream.peek.bind(stream);
  let id = '';
  let check = isIdentifierStart;
  let char = sPeek();
  let code = char.charCodeAt(0);
  while (!terminates(char) && !isEOS(char)) {
    let streamRead = false;
    if (char === '\\') {
      let nxt = sPeek(1);
      if (isEOS(nxt)) {
        throw this.createILLEGAL(char);
      }
      if (nxt !== 'u') {
        throw this.createILLEGAL(char);
      }
      code = scanUnicode(stream, 2);
      streamRead = true;
      if (code < 0) {
        throw this.createILLEGAL(char);
      }
    } else if (0xd800 <= code && code <= 0xdbff) {
      if (isEOS(char)) {
        throw this.createILLEGAL(char);
      }
      let lowSurrogateCode = sPeek(1).charCodeAt(0);
      if (0xdc00 > lowSurrogateCode || lowSurrogateCode > 0xdfff) {
        throw this.createILLEGAL(char);
      }
      stream.readString(2);
      code = decodeUtf16(code, lowSurrogateCode);
      streamRead = true;
    }
    if (!check(code)) {
      if (id.length < 1) {
        throw this.createILLEGAL(char);
      }
      return id;
    }

    if (!streamRead) stream.readString();

    id += String.fromCodePoint(code);
    char = sPeek();
    code = char.charCodeAt(0);
    check = isIdentifierPart;
  }
  return id;
}

function decodeUtf16(lead, trail) {
  return (lead - 0xd800) * 0x400 + (trail - 0xdc00) + 0x10000;
}

// @flow

import { isEOS, getCurrentReadtable } from 'readtable';
import { code } from 'esutils';
import { isTerminating, getHexValue } from './utils';
import { NumericToken } from '../tokens';

import type { CharStream } from 'readtable';

const {
  isIdentifierPartES6: isIdentifierPart,
  isIdentifierStartES6: isIdentifierStart,
} = code;

let terminates;

export default function readNumericLiteral(stream: CharStream) {
  terminates = isTerminating(getCurrentReadtable());
  let idx = 0, char = stream.peek();

  if (char === '0') {
    char = stream.peek(++idx);
    if (!isEOS(char)) {
      char = char.toLowerCase();
      switch (char) {
        case 'x':
          return readHexLiteral.call(this, stream);
        case 'b':
          return readBinaryLiteral.call(this, stream);
        case 'o':
          return readOctalLiteral.call(this, stream);
        default:
          if (isDecimalChar(char)) {
            return readLegacyOctalLiteral.call(this, stream); // reads legacy octal and decimal
          }
      }
    } else {
      return new NumericToken({
        value: +stream.readString(),
      });
    }
  } else if (char !== '.') {
    while (!terminates(char) && isDecimalChar(char)) {
      char = stream.peek(++idx);
    }
    if (isEOS(char)) {
      return new NumericToken({
        value: +stream.readString(idx),
      });
    }
  }

  idx = addDecimalLiteralSuffixLength.call(this, stream, idx);

  char = stream.peek(idx);
  if (!isEOS(char) && !terminates(char) && isIdentifierStart(char)) {
    throw this.createILLEGAL(char);
  }

  return new NumericToken({
    value: +stream.readString(idx),
  });
}

function addDecimalLiteralSuffixLength(stream, idx) {
  let char = stream.peek(idx);
  if (char === '.') {
    char = stream.peek(++idx);
    if (isEOS(char)) return idx;

    while (isDecimalChar(char)) {
      char = stream.peek(++idx);
      if (terminates(char) || isEOS(char)) return idx;
    }
  }

  if (char.toLowerCase() === 'e') {
    char = stream.peek(++idx);
    if (isEOS(char)) throw this.createILLEGAL(char);

    if (char === '+' || char === '-') {
      char = stream.peek(++idx);
      if (isEOS(char)) throw this.createILLEGAL(char);
    }

    while (isDecimalChar(char)) {
      char = stream.peek(++idx);
      if (terminates(char) || isEOS(char)) break;
    }
  }
  return idx;
}

function readLegacyOctalLiteral(stream) {
  let idx = 0, isOctal = true, char = stream.peek();

  while (!terminates(char) && !isEOS(char)) {
    if ('0' <= char && char <= '7') {
      idx++;
    } else if (char === '8' || char === '9') {
      isOctal = false;
      idx++;
    } else if (isIdentifierPart(char.charCodeAt(0))) {
      throw this.createILLEGAL(char);
    } else {
      break;
    }

    char = stream.peek(idx);
  }

  if (!isOctal)
    return new NumericToken({
      value: parseNumeric(stream, idx, 10),
      octal: true,
      noctal: !isOctal,
    });

  return new NumericToken({
    value: parseNumeric(stream, idx, 8),
    octal: true,
    noctal: !isOctal,
  });
}

function readOctalLiteral(stream) {
  let start, idx = (start = 2), char = stream.peek(idx);
  while (!terminates(char) && !isEOS(char)) {
    if ('0' <= char && char <= '7') {
      char = stream.peek(++idx);
    } else if (isIdentifierPart(char.charCodeAt(0))) {
      throw this.createILLEGAL(char);
    } else {
      break;
    }
  }

  if (idx === start) {
    throw this.createILLEGAL(char);
  }

  return new NumericToken({
    value: parseNumeric(stream, idx, 8, start),
  });
}

function readBinaryLiteral(stream) {
  let start, idx = (start = 2);
  let char = stream.peek(idx);

  while (!terminates(char) && !isEOS(char)) {
    if (char !== '0' && char !== '1') {
      break;
    }
    char = stream.peek(idx);
    idx++;
  }

  if (idx === start) {
    throw this.createILLEGAL(char);
  }

  if (
    !isEOS(char) &&
    !terminates(char) &&
    (isIdentifierStart(char) || isDecimalChar(char))
  ) {
    throw this.createILLEGAL(char);
  }

  return new NumericToken({
    value: parseNumeric(stream, idx, 2, start),
  });
}

function readHexLiteral(stream) {
  let start, idx = (start = 2), char = stream.peek(idx);
  while (!terminates(char)) {
    let hex = getHexValue(char);
    if (hex === -1) {
      break;
    }
    char = stream.peek(++idx);
  }

  if (idx === start) {
    throw this.createILLEGAL(char);
  }

  if (!isEOS(char) && !terminates(char) && isIdentifierStart(char)) {
    throw this.createILLEGAL(char);
  }

  return new NumericToken({
    value: parseNumeric(stream, idx, 16, start),
  });
}

function parseNumeric(stream, len, radix, start = 0) {
  stream.readString(start);
  return parseInt(stream.readString(len - start), radix);
}

function isDecimalChar(char) {
  return '0' <= char && char <= '9';
}

// @flow
import type { CharStream } from 'readtable';

import { isEOS } from 'readtable';
import { RegExpToken } from '../tokens';
import { isLineTerminator, isIdentifierPart } from './utils';

export default function readRegExp(stream: CharStream) {
  let value = stream.readString(),
    char = stream.peek(),
    idx = 0,
    classMarker = false,
    terminated = false;

  const UNTERMINATED_REGEXP_MSG = 'Invalid regular expression: missing /';

  while (!isEOS(char)) {
    if (char === '\\') {
      value += char;
      ++idx;
      char = stream.peek(idx);

      if (isLineTerminator(char.charCodeAt(0))) {
        throw this.createError(UNTERMINATED_REGEXP_MSG);
      }
      value += char;
      ++idx;
    } else if (isLineTerminator(char.charCodeAt(0))) {
      throw this.createError(UNTERMINATED_REGEXP_MSG);
    } else {
      if (classMarker) {
        if (char === ']') {
          classMarker = false;
        }
      } else {
        if (char === '/') {
          terminated = true;
          value += char;
          ++idx;
          char = stream.peek(idx);
          break;
        } else if (char === '[') {
          classMarker = true;
        }
      }
      value += char;
      ++idx;
    }
    char = stream.peek(idx);
  }

  if (!terminated) {
    throw this.createError(UNTERMINATED_REGEXP_MSG);
  }

  while (!isEOS(char)) {
    if (char === '\\') {
      throw this.createError('Invalid regular expression flags');
    }
    if (!isIdentifierPart(char.charCodeAt(0))) {
      break;
    }
    value += char;
    ++idx;
    char = stream.peek(idx);
  }

  stream.readString(idx);

  return new RegExpToken({
    value,
  });
}

// @flow
import type { CharStream } from 'readtable';

import { readStringEscape, isLineTerminator } from './utils';
import { isEOS } from 'readtable';
import { StringToken } from '../tokens';

export default function readStringLiteral(stream: CharStream): StringToken {
  let str = '',
    octal = null,
    idx: number = 0,
    quote = stream.readString(),
    char = stream.peek(),
    lineStart;

  while (!isEOS(char)) {
    if (char === quote) {
      stream.readString(++idx);
      if (lineStart != null) this.locationInfo.column += idx - lineStart;
      return new StringToken({ str, octal });
    } else if (char === '\\') {
      [str, idx, octal, lineStart] = readStringEscape.call(
        this,
        str,
        stream,
        idx,
        octal,
      );
    } else if (isLineTerminator(char.charCodeAt(0))) {
      throw this.createILLEGAL(char);
    } else {
      ++idx;
      str += char;
    }
    char = stream.peek(idx);
  }
  throw this.createILLEGAL(char);
}

// @flow
import { List } from 'immutable';

import type { CharStream } from 'readtable';
import { isEOS } from 'readtable';

import { readStringEscape } from './utils';
import { getSlice } from './token-reader';
import { TemplateToken, TemplateElementToken } from '../tokens';

export default function readTemplateLiteral(
  stream: CharStream,
  prefix: List<any>,
): TemplateToken {
  let element, items = [];
  stream.readString();

  do {
    element = readTemplateElement.call(this, stream);
    items.push(element);
    if (element.interp) {
      element = this.readToken(stream, List(), false);
      items.push(element);
    }
  } while (!element.tail);

  return new TemplateToken({
    items: List(items),
  });
}

function readTemplateElement(stream: CharStream): TemplateElementToken {
  let char = stream.peek(), idx = 0, value = '', octal = null;
  const startLocation = Object.assign({}, this.locationInfo, stream.sourceInfo);
  while (!isEOS(char)) {
    switch (char) {
      case '`': {
        stream.readString(idx);
        const slice = getSlice(stream, startLocation);
        stream.readString();
        return new TemplateElementToken({
          tail: true,
          interp: false,
          value,
          slice,
        });
      }
      case '$': {
        if (stream.peek(idx + 1) === '{') {
          stream.readString(idx);
          const slice = getSlice(stream, startLocation);
          stream.readString();

          return new TemplateElementToken({
            tail: false,
            interp: true,
            value,
            slice,
          });
        }
        break;
      }
      case '\\': {
        let newVal;
        [newVal, idx, octal] = readStringEscape.call(
          this,
          '',
          stream,
          idx,
          octal,
        );
        if (octal != null) throw this.createILLEGAL(octal);
        value += newVal;
        --idx;
        break;
      }
      default: {
        value += char;
      }
    }
    char = stream.peek(++idx);
  }
  throw this.createILLEGAL(char);
}

// @flow

import { CharStream, isEOS, Reader, setCurrentReadtable } from 'readtable';
import defaultReadtable from './default-readtable';
import { List } from 'immutable';
import { EmptyToken } from '../tokens';

import type { StartLocation, Slice } from '../tokens';

setCurrentReadtable(defaultReadtable);

export type LocationInfo = {
  line: number,
  column: number,
};

type Context = {
  bindings: any,
  scopesets: any,
};

export function getSlice(
  stream: CharStream,
  startLocation: StartLocation,
): Slice {
  return {
    text: stream.getSlice(startLocation.position),
    start: startLocation.position,
    startLocation,
    end: stream.sourceInfo.position,
  };
}

const streams = new WeakMap();

class ReadError extends Error {
  index: number;
  line: number;
  column: number;
  message: string;
  constructor({
    index,
    line,
    column,
    message,
  }: { index: number, line: number, column: number, message: string }) {
    super(message);
    this.index = index;
    this.line = line;
    this.column = column;
    this.message = `[${line}:${column}] ${message}`;
  }
}

class TokenReader extends Reader {
  locationInfo: LocationInfo;
  context: ?Context;
  constructor(stream: CharStream, context?: Context) {
    super();
    this.context = context;
    streams.set(this, stream);
    this.locationInfo = {
      line: 1,
      column: 1,
    };
  }

  createError(msg: string): ReadError {
    let message = msg.replace(/\{(\d+)\}/g, (_, n) =>
      JSON.stringify(arguments[+n + 1]),
    );
    return new ReadError({
      message,
      // $FlowFixMe: decide on how to handle possible nullability
      index: streams.get(this).sourceInfo.position,
      line: this.locationInfo.line,
      column: this.locationInfo.column,
    });
  }

  createILLEGAL(char) {
    return !isEOS(char)
      ? this.createError('Unexpected {0}', char)
      : this.createError('Unexpected end of input');
  }

  readToken(stream: CharStream, ...rest: Array<any>) {
    const startLocation = Object.assign(
      {},
      this.locationInfo,
      stream.sourceInfo,
    );
    const result = super.read(stream, ...rest);

    if (
      startLocation.column === this.locationInfo.column &&
      startLocation.line === this.locationInfo.line
    ) {
      this.locationInfo.column +=
        stream.sourceInfo.position - startLocation.position;
    }

    if (result === EmptyToken) return result;

    if (!List.isList(result)) result.slice = getSlice(stream, startLocation);

    return result;
  }

  readUntil(
    close: ?Function | ?string,
    stream: CharStream,
    prefix: List<any>,
    exprAllowed: boolean,
  ): List<any> {
    let result, results = prefix, done = false;
    do {
      if (isEOS(stream.peek())) break;
      done = typeof close === 'function' ? close() : stream.peek() === close;
      result = this.readToken(stream, results, exprAllowed);

      if (result !== EmptyToken) {
        results = results.push(result);
      }
    } while (!done);
    return results;
  }

  incrementLine(): void {
    this.locationInfo.line += 1;
    this.locationInfo.column = 1;
  }
}

export default function read(
  source: string | CharStream,
  context?: Context,
): List<any> {
  const stream = typeof source === 'string' ? new CharStream(source) : source;
  if (isEOS(stream.peek())) return List();
  return new TokenReader(stream, context).readUntil(
    null,
    stream,
    List(),
    false,
  );
}

// @flow

import { isEOS } from 'readtable';
import { List } from 'immutable';

import type { Readtable, CharStream } from 'readtable';

import { code } from 'esutils';
import type { TokenTree } from '../tokens';
import {
  getLineNumber,
  isPunctuator,
  isKeyword,
  isBraces,
  isParens,
  isIdentifier,
} from '../tokens';

const {
  isLineTerminator,
  isWhiteSpace,
  isDecimalDigit,
  isIdentifierPartES6: isIdentifierPart,
  isIdentifierStartES6: isIdentifierStart,
} = code;

import * as R from 'ramda';
import { Maybe } from 'ramda-fantasy';
const Nothing = Maybe.Nothing;

// TODO: also, need to handle contextual yield
const literalKeywords = ['this', 'null', 'true', 'false'];

export {
  isLineTerminator,
  isWhiteSpace,
  isDecimalDigit,
  isIdentifierStart,
  isIdentifierPart,
};

export function getHexValue(rune: string) {
  if ('0' <= rune && rune <= '9') {
    return rune.charCodeAt(0) - 48;
  }
  if ('a' <= rune && rune <= 'f') {
    return rune.charCodeAt(0) - 87;
  }
  if ('A' <= rune && rune <= 'F') {
    return rune.charCodeAt(0) - 55;
  }
  return -1;
}

export function skipSingleLineComment(stream: CharStream): void {
  let idx = 0;
  let char = stream.peek(idx);
  while (!isEOS(char)) {
    let chCode = char.charCodeAt(0);
    if (isLineTerminator(chCode)) {
      ++idx;
      if (chCode === 0xd /* "\r" */ && stream.peek(idx).charCodeAt(0) === 0xa) {
        /*"\n" */ ++idx;
      }
      this.incrementLine();
      break;
    }
    ++idx;
    char = stream.peek(idx);
  }
  stream.readString(idx);
}

export function scanUnicode(stream: CharStream, start: number) {
  const sPeek = stream.peek.bind(stream);
  let idx = start;
  let hexDigits = 0;
  if (sPeek(idx) === '{') {
    //\u{HexDigits}
    ++idx;
    let char = sPeek(idx);
    while (!isEOS(char)) {
      let hex = getHexValue(char);
      if (hex === -1) break;
      hexDigits = (hexDigits << 4) | hex;
      if (hexDigits > 0x10ffff) {
        throw this.createILLEGAL(char);
      }
      char = sPeek(++idx);
    }
    if (char !== '}') {
      throw this.createILLEGAL(char);
    }
    if (idx === start + 1) {
      throw this.createILLEGAL(stream.peek(idx + 1));
    }
    ++idx;
  } else {
    //\uHex4Digits
    if (isEOS(sPeek(idx + 3))) return -1;
    let r;
    for (; idx < start + 4; ++idx) {
      r = getHexValue(sPeek(idx));
      if (r === -1) return -1;
      hexDigits = (hexDigits << 4) | r;
    }
  }
  stream.readString(idx);

  return hexDigits;
}

export function readStringEscape(
  str: string,
  stream: CharStream,
  start: number,
  octal: ?string,
) {
  let idx = start + 1, char = stream.peek(idx), lineStart;
  if (isEOS(char)) throw this.createILLEGAL(char);

  if (!isLineTerminator(char.charCodeAt(0))) {
    switch (char) {
      case 'b':
        str += '\b';
        ++idx;
        break;
      case 'f':
        str += '\f';
        ++idx;
        break;
      case 'n':
        str += '\n';
        ++idx;
        break;
      case 'r':
        str += '\r';
        ++idx;
        break;
      case 't':
        str += '\t';
        ++idx;
        break;
      case 'v':
        str += '\u000B';
        ++idx;
        break;
      case 'u':
      case 'x': {
        let unescaped;
        ++idx;
        let nxt = stream.peek(idx);
        if (isEOS(nxt)) {
          throw this.createILLEGAL(nxt);
        }
        unescaped = char === 'u'
          ? scanUnicode.call(this, stream, idx)
          : scanHexEscape2.call(this, stream);
        if (unescaped === -1) throw this.createILLEGAL(char);
        idx = 0; // stream is read in scanUnicode and scanHexEscape2

        str += String.fromCodePoint(unescaped);
        break;
      }
      default: {
        if ('0' <= char && char <= '7') {
          [str, idx, octal] = scanOctal.call(
            this,
            str,
            stream,
            char,
            idx,
            octal,
          );
        } else if (char === '8' || char === '9') {
          throw this.createILLEGAL(char);
        } else {
          str += char;
          ++idx;
        }
      }
    }
  } else {
    if (char === '\r' && stream.peek(idx + 1) === '\n') {
      ++idx;
    }
    ++idx;
    this.incrementLine();
    lineStart = idx;
  }
  return [str, idx, octal, lineStart];
}

function scanOctal(str, stream, char, start, octal) {
  let len = 1, idx = start;
  if ('0' <= char && char <= '3') {
    len = 0;
  }
  let code = 0;

  while (len < 3 && '0' <= char && char <= '7') {
    ++idx;
    if (len > 0 || char !== '0') {
      if (octal == null) octal = '';
      octal += char;
    }
    code *= 8;
    code += +char; //coersion
    ++len;
    char = stream.peek(idx);
    if (isEOS(char)) {
      throw this.createILLEGAL(char);
    }
  }
  str += String.fromCharCode(code);
  return [str, idx, octal];
}

function scanHexEscape2(stream, idx) {
  let char = stream.peek(idx);

  if (isEOS(char)) return -1;

  let r1 = getHexValue(stream.peek());
  if (r1 === -1) return r1;

  let r2 = getHexValue(stream.peek(1));
  if (r2 === -1) return r2;

  stream.readString(2);
  return (r1 << 4) | r2;
}

export function insertSequence(coll: Object, seq: string) {
  const char = seq[0];
  if (!coll[char]) {
    coll[char] = {};
  }
  if (seq.length === 1) {
    coll[char].isValue = true;
    return coll;
  } else {
    coll[char] = insertSequence(coll[char], seq.slice(1));
    return coll;
  }
}

export const isTerminating = (table: Readtable) => (char: string): boolean =>
  table.getMapping(char).mode === 'terminating';

// check for terminating doesn't work if it's at the start
export function retrieveSequenceLength(
  table: Object,
  stream: CharStream,
  idx: number,
): number {
  const char = stream.peek(idx);
  if (!table[char]) {
    if (table.isValue) return idx;
    return -1;
  } else {
    return retrieveSequenceLength(table[char], stream, ++idx);
  }
}

const assignOps = [
  '=',
  '+=',
  '-=',
  '*=',
  '/=',
  '%=',
  '<<=',
  '>>=',
  '>>>=',
  '&=',
  '|=',
  '^=',
  ',',
];

const binaryOps = [
  '+',
  '-',
  '*',
  '/',
  '%',
  '<<',
  '>>',
  '>>>',
  '&',
  '|',
  '^',
  '&&',
  '||',
  '?',
  ':',
  '===',
  '==',
  '>=',
  '<=',
  '<',
  '>',
  '!=',
  '!==',
  'instanceof',
];

const unaryOps = [
  '++',
  '--',
  '~',
  '!',
  'delete',
  'void',
  'typeof',
  'yield',
  'throw',
  'new',
];

const allOps = assignOps.concat(binaryOps).concat(unaryOps);

function isNonLiteralKeyword(t: TokenTree) {
  return isKeyword(t) && t.value && !R.contains(t.value, literalKeywords);
}
const exprPrefixKeywords = [
  'instanceof',
  'typeof',
  'delete',
  'void',
  'yield',
  'throw',
  'new',
  'case',
];

function isExprReturn(l: number, p: List<TokenTree>) {
  // ... return {x: 42} /r /i
  // ... return\n{x: 42} /r /i
  return popRestMaybe(p)
    .map(
      ([retKwd, rest]) =>
        isKeyword(retKwd, 'return') && getLineNumber(retKwd) === l,
    )
    .getOrElse(false);
}

// List a -> Boolean
function isTopPunctuator(p: List<TokenTree>) {
  return popMaybe(p).map(punc => isPunctuator(punc)).getOrElse(false);
}

function isOperator(op: TokenTree) {
  if ((isPunctuator(op) || isKeyword(op)) && op.value != null) {
    const opVal = op.value; // the const is because flow doesn't know op.value isn't mutated
    return allOps.some(o => o === opVal);
  }
  return false;
}

function isTopOperator(p: List<TokenTree>) {
  return popMaybe(p)
    .map(op => {
      return isOperator(op);
    })
    .getOrElse(false);
}

function isExprPrefixKeyword(kwd: TokenTree) {
  return isKeyword(kwd, exprPrefixKeywords);
}

function isTopKeywordExprPrefix(p: List<TokenTree>) {
  return popMaybe(p)
    .map(kwd => {
      return isExprPrefixKeyword(kwd);
    })
    .getOrElse(false);
}

function isTopColon(p: List<TokenTree>) {
  return popMaybe(p)
    .map(colon => {
      if (isPunctuator(colon, ':')) {
        return true;
      }
      return false;
    })
    .getOrElse(false);
}

export function isExprPrefix(l: number, b: boolean, p: List<TokenTree>) {
  if (p.size === 0) {
    // ... ({x: 42} /r/i)
    return b;
  } else if (isTopColon(p)) {
    // ... ({x: {x: 42} /r/i })
    return b;
  } else if (isTopKeywordExprPrefix(p)) {
    // ... throw {x: 42} /r/i
    return true;
  } else if (isTopOperator(p)) {
    // ... 42 + {x: 42} /r/i
    return true;
  } else if (isTopPunctuator(p)) {
    // ... for ( ; {x: 42}/r/i)
    return b;
  } else if (isExprReturn(l, p)) {
    // ... return {x: 42} /r /i
    // ... return\n{x: 42} /r /i
    return true;
  }
  return false;
}

function popMaybe<T>(p: List<T>): Maybe<T> {
  if (p.size >= 1) {
    return Maybe.of(p.last());
  }
  return Nothing();
}

function isTopStandaloneKeyword(prefix: List<TokenTree>) {
  // P . t . t'  where t \not = "." and t'  (Keyword \setminus  LiteralKeyword)
  return popRestMaybe(prefix)
    .map(([kwd, rest]) => {
      if (isNonLiteralKeyword(kwd)) {
        return Maybe.maybe(
          true,
          dot => !isPunctuator(dot, '.'),
          popMaybe(rest),
        );
      }
      return false;
    })
    .getOrElse(false);
}

function isTopParensWithKeyword(prefix: List<TokenTree>) {
  // P . t . t' . (T)  where t \not = "." and t'  (Keyword \setminus LiteralKeyword)
  return popRestMaybe(prefix)
    .chain(
      ([paren, rest]) => (isParens(paren) ? popRestMaybe(rest) : Nothing()),
    )
    .map(([kwd, rest]) => {
      if (isNonLiteralKeyword(kwd)) {
        return Maybe.maybe(
          true,
          dot => !isPunctuator(dot, '.'),
          popMaybe(rest),
        );
      }
      return false;
    })
    .getOrElse(false);
}

function popRestMaybe(p: List<TokenTree>): Maybe<[TokenTree, List<TokenTree>]> {
  if (p.size > 0) {
    let last = p.last();
    let rest = p.pop();
    return Maybe.of([last, rest]);
  }
  return Nothing();
}

function isTopFunctionExpression(
  prefix: List<TokenTree>,
  exprAllowed: boolean,
) {
  // P . function^l . x? . () . {}     where isExprPrefix(P, b, l) = false
  return popRestMaybe(prefix)
    .chain(([curly, rest]) => {
      if (isBraces(curly)) {
        return popRestMaybe(rest);
      }
      return Nothing();
    })
    .chain(([paren, rest]) => {
      if (isParens(paren)) {
        return popRestMaybe(rest);
      }
      return Nothing();
    })
    .chain(([optIdent, rest]) => {
      if (isIdentifier(optIdent)) {
        return popRestMaybe(rest);
      }
      return Maybe.of([optIdent, rest]);
    })
    .chain(([fnKwd, rest]) => {
      if (isKeyword(fnKwd, 'function')) {
        let l = getLineNumber(fnKwd);
        if (l == null) {
          throw new Error('Un-expected null line number');
        }
        return Maybe.of(!isExprPrefix(l, exprAllowed, rest));
      }
      return Maybe.of(false);
    })
    .getOrElse(false);
}

function isTopObjectLiteral(prefix: List<TokenTree>, exprAllowed: boolean) {
  // P . {T}^l  where isExprPrefix(P, b, l) = false
  return popRestMaybe(prefix)
    .chain(([braces, rest]) => {
      if (isBraces(braces)) {
        let l = getLineNumber(braces);
        if (l == null) {
          throw new Error('Un-expected null line number');
        }
        return Maybe.of(!isExprPrefix(l, exprAllowed, rest));
      }
      return Maybe.of(false);
    })
    .getOrElse(false);
}

function isTopFunction(prefix: List<TokenTree>) {
  // P . function^l . x? . () . {}     where isExprPrefix(P, b, l) = false
  return popRestMaybe(prefix)
    .chain(([curly, rest]) => {
      if (isBraces(curly)) {
        return popRestMaybe(rest);
      }
      return Nothing();
    })
    .chain(([paren, rest]) => {
      if (isParens(paren)) {
        return popRestMaybe(rest);
      }
      return Nothing();
    })
    .chain(([optIdent, rest]) => {
      if (isIdentifier(optIdent)) {
        return popRestMaybe(rest);
      }
      return Maybe.of([optIdent, rest]);
    })
    .chain(([fnKwd, rest]) => {
      if (isKeyword(fnKwd, 'function')) {
        return Maybe.of(true);
      }
      return Maybe.of(false);
    })
    .getOrElse(false);
}

export function isRegexPrefix(exprAllowed: boolean, prefix: List<TokenTree>) {
  if (prefix.isEmpty()) {
    // 
    return true;
  } else if (isTopPunctuator(prefix)) {
    // P . t   where t  Punctuator
    return true;
  } else if (isTopStandaloneKeyword(prefix)) {
    // P . t . t'  where t \not = "." and t'  (Keyword \setminus  LiteralKeyword)
    return true;
  } else if (isTopParensWithKeyword(prefix)) {
    // P . t . t' . (T)  where t \not = "." and t'  (Keyword \setminus LiteralKeyword)
    return true;
  } else if (isTopFunction(prefix)) {
    // P . function^l . x? . () . {}     where isExprPrefix(P, b, l) = false
    return isTopFunctionExpression(prefix, exprAllowed);
  } else if (isTopObjectLiteral(prefix, exprAllowed)) {
    // P . {T}^l  where isExprPrefix(P, b, l) = false
    return true;
  }
  return false;
}
